 // Lava floor with ocean-like waves and smoke
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.xy;

    float time = iTime;

    // Lava floor
    vec3 lavaColor = vec3(1.0, 0.3, 0.0); 
    float lavaHeight = 0.3 + 0.1*sin(uv.x*10.0 + time*2.0);
    float lava = smoothstep(lavaHeight, lavaHeight + 0.02, uv.y);

    // Ocean-like waves
    vec3 oceanColor = vec3(0.0, 0.5, 1.0);
    float waves = 0.05*sin(uv.x*10.0 + time)*sin(uv.y*10.0 + time);

    // Smoke
    float smoke = 0.3*smoothstep(0.3, 0.5, length(uv - vec2(0.5 + 0.2*sin(time), 0.5 + 0.1*cos(time))));
    vec3 smokeColor = vec3(0.5);

    vec3 finalColor = mix(lavaColor, oceanColor, waves) * (1.0 - lava) + smokeColor*smoke;

    fragColor = vec4(finalColor, 1.0);
}
