//golfed version by Fabrice Neyret (from 450 to 346 chars)
#define r(a)  mat2(cos(a+vec4(0,11,33,0)))

void mainImage(out vec4 o, vec2 u)
{
    vec3 P,R = iResolution;
    float i,d=1.,a,g, t=iTime;

    for( o*=i ; i++ < 99. && d > 1e-4; g += d*.5 )
    {
        P = g * vec3( (u-R.xy*.5)/R.y , 1 );   
        P.zy = P.zy*r(1.)
             + sin(t*6.3) *.07;
        d = min( ( (P.y+=3.) - abs( fract(P.z+=t) -.5 ) ) *.7
                 , 1.5 - abs(P.x)
               );
        for( a = 2. ; a < 6e2 ; a += a )
            P += P , 
            P.xz *= r(a),
            d += abs( dot( sin(P) , R/R ) ) /a/7.;
    }
    o += 9./i;
}
