// TetraPack Visualizer
// Sourced: https://shadertoy.com/view/NlVBRd, ttps://www.shadertoy.com/view/ftKBzt

// Made compatible with iOS

#define R(p,a,t) mix(a*dot(p,a),p,cos(t))+sin(t)*cross(p,a)
#define H(h)     (cos(max(1.,1.1)*1.3*h+vec3(5,25,21))*.7+.2)

vec3 hueShift(vec3 col, float shift) {
    vec3 m = vec3(cos(shift), -sin(shift) * .57735, 0);
    m = vec3(m.xy, -m.y) + (1. - m.x) * .33333;
    return mat3(m, m.zxy, m.yzx) * col;
}

vec3 aces(vec3 x) {
    return clamp((x * (2.51 * x + 0.03)) / (x * (2.43 * x + 0.59) + 0.14), 0.0, 1.0);
}

void mainImage( out vec4 O, vec2 C)
{ 
    O=vec4(0);
    vec2 m = iMouse.xy/iResolution.xy;
    float bass=texture(iChannel0, vec2(.1,.35)).r;
    float snd =texture(iChannel0, vec2(sqrt(C.x*C.x+C.y*C.y),.75)).r;
    float t = iTime;
    vec3 r=iResolution;
    vec3 d=normalize(vec3((1.-.5*bass)*(C-.5*r.xy)/r.y,1))*5.;  

    for(
        float i=0.,g=0.,e,s;
        ++i<115.;
        O.rgb+=mix(vec3(1) ,H(log(s)),.7)*.015*exp(-i*i*e*e))
    {
         vec4 p=vec4(g*d,0.);
        p.xyz=R(p.xyz+vec3(0.,.1,-2.5),normalize(H(t*.05+sqrt(m.x*m.x+m.y*m.y))),t);
        s=1.;
        for(float j=0.;j<7.;j++) {  
            p= abs(p)*0.6208;
            s*=e=max(1./dot(p,p),.4*(snd*snd)),
            p=abs(p.x<p.y?p.wzxy:p.wzyx)*e - 1.1;  
        }
        g+=e=abs(length(p.yzw*p.x)-.0)/s+.2e-4;

    }
    O=vec4(pow(O.rgb,vec3(2)),1.);
 }
