    #else
    col *= 1.-dot(uv,uv);
    t = mod(iTime, 10.);

    #endif
	vec3 from=vec3(1.,.5,0.5)*col;

	mainVR(O, C, from, dir);	
   O*=vec4(col*10.,1.);
    vec3 p,q,r=iResolution,
    d=normalize(vec3((C*2.-r.xy)/r.y,1));  
    for(float i=0.,a,s,e,g=0.;
        ++i<70.;
        O.xyz+=mix(vec3(1),H(g*.1),.8)*1./e/8e3
    )
