            s*=e=1.2+sin(iTime*.01)*.1;
            p=abs(p)*e-
                vec3(
                    5.+sin(iTime*.0+.5*sin(iTime*.3))*3.,
                    120,
                    8.+cos(iTime*.05)*5.
                 )+col;
         }
         g+=e=length(p.yzyz)/s;
    }
