const int MAX_RAY_STEPS = 64;


float sdSphere(vec3 p, float d) { return length(p) - d; } 

float sdBox( vec3 p, vec3 b ) {
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) +
         length(max(d,0.0));
}

bool getVoxel(ivec3 c) {
	vec3 p = vec3(c) + vec3(0.5);
	float d = min(max(-sdSphere(p, 7.5), sdBox(p, vec3(6.0))), -sdSphere(p, 25.0));
	return d < 0.0;
}

vec2 rotate2d(vec2 v, float a) {
	float sinA = sin(a);
	float cosA = cos(a);
	return vec2(v.x * cosA - v.y * sinA, v.y * cosA + v.x * sinA);	
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 screenPos = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;
	vec3 cameraDir = vec3(0.0, 0.0, 1.0);
	vec3 cameraPlaneU = vec3(1.0, 0.0, 0.0);
	vec3 cameraPlaneV = vec3(0.0, 1.0, 0.0) * iResolution.y / iResolution.x;
	vec3 rayDir = cameraDir + screenPos.x * cameraPlaneU + screenPos.y * cameraPlaneV;
	vec3 rayPos = vec3(0.0, 2.0 * sin(iTime * 2.7), -12.0);

	rayPos.xz = rotate2d(rayPos.xz, iTime);
	rayDir.xz = rotate2d(rayDir.xz, iTime);

	bvec3 mask;
    vec3 mapPos = floor(rayPos);
    vec3 raySign = sign(rayDir);
    vec3 deltaDist = 1.0/rayDir;
    vec3 sideDist = ((mapPos-rayPos) + 0.5 + raySign * 0.5) * deltaDist;

	for (int i = 0; i < MAX_RAY_STEPS; i++) {
        bvec3 b1 = lessThan(sideDist.xyz, sideDist.yzx);
        bvec3 b2 = lessThanEqual(sideDist.xyz, sideDist.zxy);
        mask.z = b1.z && b2.z;
        mask.x = b1.x && b2.x;
        mask.y = b1.y && b2.y;
        // if (mask == bvec3(false, false, false))
        if(!any(mask)) // Thank you Spalmer
            mask.z = true;

        vec3 vmask = vec3(mask);
        mapPos +=  vmask * vec3(raySign);

        if (getVoxel(ivec3(mapPos))) {
            vec3 mini = ((mapPos-rayPos) + 0.5 - 0.5*vec3(raySign))*deltaDist;
            float distance = max (mini.x, max (mini.y, mini.z));

            vec3 intersect = rayPos + rayDir*distance;
            vec3 uv3d = intersect - mapPos;
            vec2 uv2d = vec2(dot(vmask.yzx, uv3d), dot(vmask.zxy, uv3d));

            fragColor = vec4(uv2d, 0.0, 1.0);
            return;
        }

        sideDist += vmask * raySign * deltaDist;
	}

}
