vec3 erot(vec3 p,vec3 ax,float t){return mix(dot(ax,p)*ax,p,cos(t))+cross(ax,p)*sin(t);}
vec3 pcg3d(vec3 p){
    uvec3 q = floatBitsToUint(p)*1234567u+134679852u;
    q.x+=q.y*q.z;q.y+=q.x*q.z;q.z+=q.y*q.x;
    q^=q>>16u;
    q.x+=q.y*q.z;q.y+=q.x*q.z;q.z+=q.y*q.x;
    return vec3(q)/float(-1u);
}
float box(vec2 p,vec2 b){p=abs(p)-b;return length(max(vec2(.0),p))+min(0.,max(p.x,p.y));}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float bpm = iTime*140./60.*.5;
    vec2 uv = (fragCoord.xy -.5* iResolution.xy)/iResolution.y;
    vec3 rrnd = pcg3d(vec3(uv,bpm));
    uv*=1.+smoothstep(.2,.4,abs(uv.y))*rrnd.xy*.05;
    uv = erot(uv.xyy,vec3(0.,0.,1.)+rrnd*.05,tanh(sin(bpm*.5)*16.)).xy;
    uv*=8.;
    uv.x +=bpm+floor(bpm)+smoothstep(.1,.9,fract(bpm));
    vec2 id = floor(uv);
    vec3 rnd = pcg3d(id.xyy);
    rnd = pcg3d(id.xyy+vec3(floor(bpm+rnd.x+rrnd.x*.1)));
    vec2 guv = fract(uv)-.5;

    float sc= 1.;

    for(float i=0.;i++<5. && rnd.z >.5;) {
        guv*=2.;
        sc*=2.;
        id = floor(guv);
        rnd = pcg3d(id.xyy+rnd).yzx;
        guv = fract(guv)-.5;
        guv= erot(guv.xyy,vec3(0.,0.,1.),step(.5,rnd.x)*.785).xy;
    }

    float d = abs(length(guv)-.33);
    d= rnd.x > 0.5 ? d: min(abs(guv.y),abs(guv.x));
    d= rnd.y > 0.5 ? d: abs(box(guv,vec2(.45)))-.001;
    d = .005/(.005+d/sc);smoothstep(.01,0.009/sc,d/sc);
    vec3 col = 8.*vec3(rnd*exp(-5.*fract(bpm+rnd.y)))*d;

    fragColor = vec4(pow(col+rrnd*.1,vec3(.7,.7,.7)),1.0);

}
