#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h)(cos((h)*6.3+vec3(0,23,21))*.5+.5)


const int MAXITER = 77;

vec3 field(vec3 p) {
	p *= .1;
	float f = .1;
	for (int i = 0; i < 5; i++) {
		p = p.yzx*mat3(.8,.6,0,-.6,.8,0,0,0,1);
		p += vec3(.123*cos(iTime*0.021)*float(i),.456*sin(iTime*0.021)*float(i),.789)*float(i);
		p = abs(fract(p)-.5);
		p *= 2.0;
		f *= 2.0;
	}
	p *= p;
	return sqrt(p+p.yzx)/f-.007;
}
#define time iTime
#define resolution iResolution.xy
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    vec3 dir = normalize(vec3((gl_FragCoord.xy-resolution*.5)/resolution.x,1.));
	vec3 pos = vec3(7.,7.,-time);
	vec3 color = vec3(0);
	for (int i = 0; i < MAXITER; i++) {
		vec3 f2 = field(pos);
		float f = min(min(f2.x,f2.y),f2.z);

		pos += dir*f;
		color += float(MAXITER-i)/(f2+.1);
	}
	vec3 color3 = vec3(1.-007./(7.+color*(.777/float(MAXITER*MAXITER))));

    vec3 p,q,r=iResolution,
    d=normalize(vec3((C*3.-r.xy)/r.y,1));  
    for(float i=0.,a,s,e,g=0.;
        ++i<70.;
        O.xyz+=mix(vec3(1),H(g*.1),.7)*10./e/8e3
    )
    {
        p=g*d*color3;

        a=30.;
        p=mod(p-a,a*3.)-a;
        s=2.;
        for(int i=0;i++<8;){
            p=.5-abs(p);
            p.x<p.z?p=p.zyx:p;
            p.z<p.y?p=p.xzy:p;
            s*=e=1.7+sin(iTime*.1)*.1;
            p=abs(p)*e-
                vec3(
                    10.*3.,
                    360,
                    5.*7.
                 );
         }
         g+=e=length(p.yz)/s;
    }
}
