uniform float time;
uniform vec2 mouse;
uniform vec2 resolution;
#define resolution iResolution.xy
#define time iTime
#define PI 3.141592
mat2 rotate(float a) {
    return mat2(cos(a),-sin(a),sin(a),cos(a));
}
float atan2(vec2 u){return atan(u.x,u.y);}

void mainImage(out vec4 O, vec2 u) {
    u = abs(u+u-(O.xy=iResolution.xy))/O.y; O *= 0.;

    vec2 uv=(gl_FragCoord.xy*2.-resolution.xy)/min(resolution.x,resolution.y);
    float t = iTime * .1 + ((.25 + .05 * sin(iTime * .1))/(length(uv.xy) + .07)) * 2.2;
float si = sin(t);
float co = cos(t);
mat2 ma = mat2(co, si, -si, co);
    uv*=ma;
float s=exp2(fract(time));//pow(2.,fract(time)+1.); //Zoom
uv*=s;
float c=7.; //Count
float r=exp2(floor(log2(length(uv))-(1./c))-1.); // pow(2.,floor(log2(length(uv))-(1./c))-1.); //Radius
float d=r/sin(PI/c); //Space distance
float a=(atan2(rotate(PI/2.)*uv)+PI)/(PI*2.);
a=floor(fract(a+(.5/c))*c)/c;
vec2 uv2=uv*ma*rotate(a*PI*2.);
float b=length(uv2-vec2(d,0));
float v = (b-r)*(2./s);
u*=ma;
    for (float i = 0., t = iTime*.5; i < 46.; O += .002/
        abs(abs(u.x-sin(t+i*.17)*.7*v)+u.y-sin(t+i*.1)*.6)
        *(cos(i+++vec4(0,1,2,0)*v)+1.2));
}
