mat2 rotate2D(float angle)
{
    return mat2(cos(angle), -sin(angle),
                sin(angle), cos(angle));
}

float radar(vec2 uv, vec2 center)
{
    const float tau =atan(1.)*8.;
    vec2 v = uv - center;
    float theta = atan(v.y, v.x);  
    float ret;
    ret = mod(theta + -iTime*1.5, tau);
    return ret;
}


vec2 rand(vec2 uv) {
    return floor(abs(mod(cos(
        uv * 652.6345 + uv.yx*cos(iTime) * 534.375 +
        -iTime * 0.0000005 * dot(uv, vec2(0.364, 0.934))),
     0.001)) * 16000.0);
}

float fallerSpeed(float col, float faller) {
    return mod(cos(col * 363.435  + faller * 234.323), 0.1) * 1.0 + 0.3;
}


