    vec4 p;
    vec2 c23 = rand(vec2(p.w*floor(p.x), p.z));
    vec3 q,r=iResolution,
    d=normalize(vec3((C*2.-r.xy)/r.y,1));  
    for(float i=0.,a,s,e,g=0.;
        ++i<110.;
        O.xyz+=mix(vec3(1),H(g*.1),sin(.8))*1./e/8e3
    )
    {
        p.ywz=g*d+c23.x;
        p.w+=iTime*0.1;
   p.wz=p.wz*=mat2(cos(iTime),sin(iTime), -sin(iTime) ,cos(iTime))*(-1.);
    p.yz*=mat2(cos(iTime),sin(iTime), -sin(iTime) ,cos(iTime));
        a=30.*c23.y;

        p.xyz=mod(p.wzy-a,a*2.)-a;
