#define INFINITE_MASS_AT_CENTER (true)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0.4);
    vec2 uv = fragCoord / iResolution.x;
    vec2 uv2 = mod(uv, vec2(0.5, 1.0));
    vec4 m = iMouse / iResolution.x;
    vec4 m2 = mod(m, vec4(0.5, 1.0, 0.5, 1.0));
    float r = 0.1;

    vec2 pos = vec2(m2.xy); // Self particle
    vec2 pos_j = vec2(0.25, 0.28); // Neighbor particle

    if (length(pos - uv2) < r) col = vec3(0.9, 0.7, 0.7);
    if (length(pos_j - uv2) < r) col = vec3(0.7, 0.85, 0.7);
    if (length(pos - uv2) < r && length(pos_j - uv2) < r) col = vec3(1.0, 0.9, 0.5);

    {
        // Inverse masses
        float w = 1.0 / 1.0;
        float w_j = INFINITE_MASS_AT_CENTER ? 0.0 : 1.0 / 1.0;

        float restDist = r * 2.0;
        vec2 pos_ij = pos - pos_j;
        vec2 normal = normalize(pos_ij);
        float depth = restDist - length(pos_ij);
        vec2 displacement = normal * depth / (w + w_j);
        vec2 newPos = pos + displacement * w;
        vec2 newPos_j = pos_j - displacement * w_j;

        // Outlines show resolution state.
        float gap = 0.005;
        bool intersection = depth > 0.0;
        if (uv.x < 0.495 && intersection && length(newPos - uv) < r && length(newPos - uv) > r - gap) col = vec3(1.0, 0.4, 0.4);
        if (uv.x < 0.495 && intersection && length(newPos_j - uv) < r && length(newPos_j - uv) > r - gap) col = vec3(0.4, 1.0, 0.4);
    }

    {
        // Inverse masses
        float w = 1.0 / 1.0;
        float w_j = INFINITE_MASS_AT_CENTER ? 0.0 : 1.0 / 1.0;

        vec2 m3 = abs(m.zw);
        vec2 m4 = mod(m3, vec2(0.5, 1.0));
        vec2 normal2 = normalize(m4.xy - pos_j);

        float restDist = r * 2.0;
        vec2 pos_ij = pos - pos_j;
        vec2 normal = normal2;
        vec2 tangent = vec2(-normal.y, normal.x);
        float depth = (restDist - dot(pos_ij, normal));
        float touchOffset = (1.0 - sqrt(1.0 - min(pow(dot(pos_ij, tangent) / restDist, 2.0), 1.0))) * restDist;
        vec2 displacement = normal * (depth - touchOffset) / (w + w_j);
        vec2 newPos = pos + displacement * w;
        vec2 newPos_j = pos_j - displacement * w_j;

        // Darken the backside hemisphere
        if (uv.x > 0.505 && length(pos - uv2) < r) {
            if (dot(uv2 - pos, normal2) > 0.0) col *= 0.8;
        }
        if (uv.x > 0.505 && length(pos_j - uv2) < r) {
            if (dot(uv2 - pos_j, normal2) < 0.0) col *= 0.8;
        }

        // Outlines show resolution state.
        float gap = 0.005;
        bool intersection = depth > 0.0;
        if (uv.x > 0.505 && intersection && length(newPos - uv2) < r && length(newPos - uv2) > r - gap) col = vec3(1.0, 0.4, 0.4);
        if (uv.x > 0.505 && intersection && length(newPos_j - uv2) < r && length(newPos_j - uv2) > r - gap) col = vec3(0.4, 1.0, 0.4);
    }

    if (uv.x > 0.495 && uv.x < 0.505) { col = vec3(0.8); }

    fragColor = vec4(col,1.0);
}
