// variant + golfing 966 chars "Kuramoto model" by zenzicubic. https://shadertoy.com/view/lXS3Wy
// The Kuramoto model of coupled oscillators, inspired by https://www.complexity-explorables.org/explorables/spin-wheels/

void mainImage(out vec4 O, vec2 U) {
    O = .5 + .5 * cos(  T(U/4.) //  texture(iChannel0,U/4./iResolution.xy).x 
                      + vec4(0, .628, 1.257, 0));
}

void mainImage(out vec4 O, vec2 U) {  
    float t = T(U), d;

    for(int k; k<9; k++ ) { // sum neighborhood, weigth = .5 in diags
        vec2 D = vec2(k%3,k/3)-1.;
        d += cos( T(U + D * (.5+.5*mod(D.x+D.y,2.)) ) - t );
    }     // ^sin, in the original Kuramoto model 

    O.x = iFrame < 1
             ? 6.28 * fract(1e4*sin(dot(U,vec2(73,-37)))) // init = rand
          // : mod( t + .1 * (d * .05 + .5), 6.28 );
             :      t + .1 * (d * .05 + .5);
                      // DT  COUPLING  SPEED         
}
