vec2 Resolution;

#define USE_TEMP_ACCU_COND                (ReadKeyToggle(KEY_TAB  ) != 0.0)
#define USE_UNIFORM_HEMISHPHERE_WEIGHTING (ReadKeyToggle(KEY_SPACE) != 0.0)
#define USE_PERSPECTIVE_CAM_COND          (ReadKeyToggle(KEY_CTRL ) == 0.0)
#define SHOW_UI_COND                      (ReadKeyToggle(KEY_U    ) == 0.0)

////////////////////////////////////////////////////////////////////////////////////////////////////////////// misc
//==========================================================================================================//

/* http://keycode.info/ */
#define KEY_LEFT  37
#define KEY_UP    38
#define KEY_RIGHT 39
#define KEY_DOWN  40

#define KEY_TAB 9
#define KEY_CTRL 17
#define KEY_ALT 18
#define KEY_SHIFT 16
#define KEY_SPACE 32 

#define KEY_Q 81
#define KEY_W 87
#define KEY_E 69
#define KEY_R 82
#define KEY_T 84

