//==========================================================================================================//
void Intersect_Ray_Cube(vec3 rp, vec3 rd, vec3 c0p, vec3 c0d, 
                        inout float hit, inout float t, inout vec3 n)
{
    vec2 tt; vec3 n0, n1;
    float th = Intersect_Ray_Cube(rp, rd, c0p, c0d, /*out:*/ tt, n0, n1);

    if(th > 0.0)
    if(hit <= 0.0 || tt.x < t)
    {
        t = tt.x;
        n = n0;

        hit = 1.0;
    }        
}

float Intersect_Scene(
vec3 rp, vec3 rd,
out float t, out vec3 n, out vec3 a)
{
    float hit = 0.0;

    float c1dxz = 0.4;
    float c1ps = 0.85;

    // ground plate thingy:
    //if(false)
    {
        vec3 c0p = vec3(0.0, -0.8, 0.0);
        vec3 c0d = vec3(2.0, 0.125*1.2, 2.0);//0.5

        vec3 c1p = vec3(0.0, -0.64, 0.0);
        vec3 c1d = vec3(2.5, 0.125, 1.5);
         c1d = vec3(c1dxz, 0.125, c1dxz);


        c1p.xz = (floor(rp.xz*c1ps+0.5))/c1ps;

        bool isInsideC0 = IsInsideCube(rp, c0p, c0d);
        bool isInsideC1 = IsInsideCube(rp, c1p, c1d);

        if(isInsideC0 && isInsideC1)
        {
            float t0; vec3 n0;
            Intersect_Ray_CubeBackside(rp, rd, c1p, c1d, t0, n0);

            if(IsInsideCube(rp+rd*t0, c0p, c0d))
            {
                hit = 1.0;
                t = t0;
                n = n0;
            }
        }
        else
        {
            vec2 tt; vec3 n0, n1;
            float th = Intersect_Ray_Cube(rp, rd, c0p, c0d, /*out:*/ tt, n0, n1);

            hit = th > 0.0 ? 1.0 : 0.0;
            t = tt.x;
            n = n0;
            a = vec3(1.0, 0.005, 0.005) * 0.8;

            if(hit==1.0) c1p.xz = (floor((rp.xz+rd.xz*t)*c1ps+0.5))/c1ps;
            if(hit==1.0 && IsInsideCube(rp+rd*t, c1p, c1d))
            {
                float t0; vec3 n0;
                Intersect_Ray_CubeBackside(rp, rd, c1p, c1d, t0, n0);

                if(IsInsideCube(rp+rd*t0, c0p, c0d))
                {
                    t = t0;
                    n = n0;                
                }
                else
                {
                    hit = 0.0;
                }
            }
        }
    }

    // slim pillars:
    //if(false)
    {
        float r = 0.125*0.25;
        float u = 1.0/c1ps;
        float l = 0.4;
        float h = 1.0;

        vec3 c0p = vec3(c1dxz, l, c1dxz);
         c0p = vec3(u - c1dxz, l, u - c1dxz);
         c0p = vec3(u - c1dxz, l, c1dxz);
        vec3 c0d = vec3(r, h, r);

        vec3 pa = vec3(u - c1dxz, l,     c1dxz);
        vec3 pb = vec3(u - c1dxz, l, u - c1dxz);
        vec3 pc = vec3(    c1dxz, l, u - c1dxz);

        //Intersect_Ray_Cube(rp, rd, c0p, c0d, /*inout*/ hit, t, n);
        Intersect_Ray_Cube(rp, rd, pa, c0d, /*inout*/ hit, t, n);
        Intersect_Ray_Cube(rp, rd, pb, c0d, /*inout*/ hit, t, n);
        Intersect_Ray_Cube(rp, rd, pc, c0d, /*inout*/ hit, t, n);

        pa = vec3(-pa.z, pa.y, pa.x);
        pb = vec3(-pb.z, pb.y, pb.x);
        pc = vec3(-pc.z, pc.y, pc.x);

        Intersect_Ray_Cube(rp, rd, pa, c0d, /*inout*/ hit, t, n);
        Intersect_Ray_Cube(rp, rd, pb, c0d, /*inout*/ hit, t, n);
        Intersect_Ray_Cube(rp, rd, pc, c0d, /*inout*/ hit, t, n);

        pa = vec3(-pa.z, pa.y, pa.x);
        pb = vec3(-pb.z, pb.y, pb.x);
        pc = vec3(-pc.z, pc.y, pc.x);

        #if 0
        Intersect_Ray_Cube(rp, rd, pa, c0d, /*inout*/ hit, t, n);
        Intersect_Ray_Cube(rp, rd, pb, c0d, /*inout*/ hit, t, n);
        Intersect_Ray_Cube(rp, rd, pc, c0d, /*inout*/ hit, t, n);
        #endif

        pa = vec3(-pa.z, pa.y, pa.x);
        pb = vec3(-pb.z, pb.y, pb.x);
        pc = vec3(-pc.z, pc.y, pc.x);

        Intersect_Ray_Cube(rp, rd, pa, c0d, /*inout*/ hit, t, n);
        Intersect_Ray_Cube(rp, rd, pb, c0d, /*inout*/ hit, t, n);
        Intersect_Ray_Cube(rp, rd, pc, c0d, /*inout*/ hit, t, n);
    }

    // top sphere:
    //if(false)
    {
        vec2 tt;
		float th = Intersect_Ray_Sphere(rp, rd, vec3(0.0), 0.5, /*out:*/ tt);

        if(th > 0.0)
        if(hit <= 0.0 || tt.x < t)
        {
            t = tt.x;
            n = normalize(rp + rd * tt.x);
            a = vec3(1.0);

            hit = 1.0;
        }
    }

    // bottom sphere:
    if(false)
    {
        vec2 tt;
		float th = Intersect_Ray_Sphere(rp, rd, vec3(0.0, -2.25, 0.0), 2.0, /*out:*/ tt);

        if(th > 0.0)
        if(hit <= 0.0 || tt.x < t)
        {
            t = tt.x;
            n = normalize(rp + rd * tt.x - vec3(0.0, -2.25, 0.0));
            a = vec3(1.0);

            hit = 1.0;
        }
    }

    if(hit <= 0.0)
    {
        t = exp2(20.0);
        n = vec3(0.0);
        a = vec3(0.0);
    }

    return hit;
}
