#define PI 3.14159265358


vec2 complexMul(vec2 a, vec2 b) {
    return vec2(a.x*b.x - a.y*b.y, a.x*b.y + a.y*b.x);
}

vec2 complexDiv(vec2 a, vec2 b) {
    float denom = b.x*b.x + b.y*b.y;
    return vec2(
        (a.x*b.x + a.y*b.y) / denom,
        (a.y*b.x - a.x*b.y) / denom
    );
}

vec2 complexExp(vec2 z) {
    return exp(z.x) * vec2(cos(z.y), sin(z.y));
}

vec2 transform(vec2 z) {
    float q = 21.0;
    float logp = log(3.0);

    vec2 qPower = complexExp(complexMul(z, vec2(log(cos(iTime*000.5)+21.0) / logp, 0.0)));
    vec2 term1 = complexMul(qPower, z);
    vec2 term2 = complexMul(vec2(-1.0 + qPower.x, qPower.y), vec2(logp, 0.0));
    vec2 denominator = term1 + term2;

    vec2 result = vec2(log(cos(iTime*000.5)+21.0), 0.0) - complexDiv(vec2(1.0, 0.0), denominator);
    return result;
}

vec3 palette( float t ) {
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.263,0.416,0.557);

    return a + b*cos( 6.28318*(c*t+d) );
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 z = fragCoord / iResolution.xy - vec2(0.5, 0.5);
      vec2 uv2 = fragCoord/iResolution.xy-0.5;
    float t2 = iTime * .1 + ((.25 + .05 * sin(iTime * .1))/(length(uv2.xy) + .07)) * 10.2;
	float si = sin(t2);
	float co = cos(t2);
	mat2 ma = mat2(co, si, -si, co);
     vec2 uv = (fragCoord - 0.5 * iResolution.xy) / min(iResolution.x, iResolution.y);
    uv *= 0.1; // Adjust scale
    z*=ma;
    // Create grid
    vec2 gridSize = vec2(2222.0, 2222.0);
    vec2 cell = fract(uv * gridSize) - 0.5;
    vec2 cellIndex = floor(uv * gridSize);
     vec2 uv3 = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv3;
    vec3 finalColor = vec3(0.0);

    for (float i = 0.0; i < 4.0; i++) {
        uv3 = fract(uv3 * 1.5) - 0.5;

        float d = length(uv3) * exp(-length(uv0));

        vec3 col = palette(length(uv0) + i*.4 + iTime*.4);

        d = sin(d*8. + iTime)/8.;
        d = abs(d);

        d = pow(0.01 / d, 1.2);

        finalColor += col * d;
    }
    // Apply transformation
    vec2 z2 = cellIndex / gridSize * vec2(1.0 * log(cos(iTime*000.5)+3.0) / log((77.0+sin(iTime))/2.0));
    vec2 transformed = transform(z2);

    // Draw grid lines
    float lineWidth = 0.005;
    float line = smoothstep(lineWidth, 0.0, abs(cell.x)) + smoothstep(lineWidth, 0.0, abs(cell.y));

    // Color based on transformed position
    vec3 col = 0.55 + 0.5 * cos(transformed.xyy + vec3(0, 2, 4));

    // Combine grid and color
    col = mix(col, vec3(1.0), line);
    float t = iTime / 2.5;
      z*=mat2(cos(iTime), sin(iTime), -sin(iTime), cos(iTime));
    z = vec2(length(z), abs(sin(z.y)));



    float scale = 1.;
    for (int i = 0; i < 14; i++) {

        vec2 w = z * 2.;

        if (z.x >= 0.5 && z.y <= 1.5) { w.x-=1.; }
        else if (z.y >= 0.5 && z.x <= 1.5) { w.y-=0.5; }

        scale*=2.;
        z = w;
    }
    float d = min(min(z.x, z.y), abs(1.-z.x-z.y))/scale;
    vec3 col2 = vec3(1.-smoothstep(0., 0.005, d));
    fragColor = vec4(col*col2*mod(fragCoord.x,5.)*finalColor, 1.);


}
