////////////////////////////////////////////////////////////////////////////////
#define pi acos(-1.)
#define deg pi/180.
#define time iTime*pi/10.
#define R iResolution
#define ar R.x/R.y
vec3 cs = vec3(1.,2.,3.);
mat2 r2d(float a) {
    return mat2(cos(a),sin(a),-sin(a),cos(a));
}

vec3 c1(vec2 uv, float t, float ij) {
    vec3 col = vec3(0.);
    float s = 8.;
    uv *= 1.;
    vec2 ov = uv;
    uv = uv/dot(uv,uv);
    ov = uv;
    //uv.x += sin(tv.x*29.)*1.;
    for (int i=0;i<24;i++) {
        uv *= 1.+sin(t/4.)*0.1;
        uv += vec2(sin(uv.x+t/2.)*0.4);
        uv *= r2d(deg*90./4.);
    }
    //uv += sin(length(uv)*24.)/40.;
    //uv *= r2d(time);
    uv = uv/dot(uv,uv);
    uv = (fract(uv*s-0.5)-0.5)/s;
    col += smoothstep(0.1,0.,min(abs(uv.x),abs(uv.y)));
    col = (sin(cs+col*12.+t+ij))*0.7+0.3;
    return col;
}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 col = vec3(0.);
    vec2 tv = uv;
    uv -= 0.5;
    uv.x *= ar;
    for (int i=0;i<45;i++) {
        float ii = float(i);
        uv *= 1.+sin(time+length(uv)*2.)*0.002;
        uv += vec2(sin(time+ii/9.),cos(time+ii/9.))*0.005*sin(time+length(uv)*4.);
        col += c1(uv,time+ii/130., ii*0.1)/30.;
    }
    fragColor = vec4(col,1.0);
}
