float cal(in float t , in float offset , in vec2 uv , in float dt , in float d)
{
    float o = 1.0 + iTime*0.05;
    float px = (fract(t)-fract(t-0.5))*(fract(t)-0.5)*4.0;  
    float py = fract(t-0.5)*2.0-1.0;                                    
    float l = uv.x*px+uv.y*py; 
    return (smoothstep(-0.003 , 0.0 , l) - smoothstep(0.0 , 0.003 , l))*(fract(dt+offset*o) - fract(dt+offset*o-0.5) + 0.5)*fract(d); 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)

    vec2 uv=fragCoord/iResolution.xy-.5;
	uv.y*=iResolution.y/iResolution.x;

    float d = length(uv);
    float dt = d*30.0-iTime*5.0;


    float r = 0.0;
    float g = 0.0;
    float b = 0.0;


    float offset = 0.0;
    for(int i = 0;i<20;i++)
    {
        //float p =  sin(iTime)/2.0+0.5;
        float t = -iTime*0.03+offset;
        //float p = fract(t);
        r += cal(t , offset , uv , dt , d)*1.3;
        offset+=0.05;
    }


    offset = 0.0;
    for(int i = 0;i<50;i++)
    {
        //float p =  sin(iTime)/2.0+0.5;
        float t = iTime*0.02+offset;
        //float p = fract(t);
        g += cal(t , offset , uv , dt , d)*1.2;
        offset+=0.02;
    }

    b = fract(dt*0.5)*fract(d);


    fragColor = vec4(r , g , b ,1.0);
}
