void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uv0 = uv;
    float time = iTime * 0.3;

    // Mouse interaction
    vec2 mouse = iMouse.xy/iResolution.xy;
    float mouseFactor = length(uv - mouse);

    // Distort UV with more complex patterns
    uv.x += sin(uv.y*15.0 + time) * 0.1 + cos(uv.y*7.0 - time*1.5) * 0.05;
    uv.y += cos(uv.x*15.0 + time*1.1) * 0.1 + sin(uv.x*9.0 - time*1.3) * 0.05;

    // Create base shape
    float d = length(uv - vec2(0.5));

    // Create more dynamic color
    vec3 col = 0.5 + 0.5 * cos(time + uv.xyx*vec3(3.0,4.0,5.0) + vec3(0,2,4));

    // Add some animated noise
    float n = fract(sin(dot(uv0, vec2(12.9898, 78.233))*time) * 43758.5453);
    col *= 0.8 + 0.2 * n;

    // Add pulsating effect
    float pulse = sin(time * 2.0) * 0.5 + 0.5;
    col *= 0.8 + 0.2 * pulse;

    // Shape the color based on distance and mouse position
    col *= smoothstep(0.8, 0.2, d + mouseFactor * 0.2);

    // Add edge highlight
    float edge = smoothstep(0.5, 0.4, d) - smoothstep(0.4, 0.3, d);
    col += edge * vec3(1.0, 0.7, 0.3) * (sin(time) * 0.5 + 0.5);

    // Output to screen
    fragColor = vec4(col, 1.0);
}
