//Buffer A is where it is at
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 uv = fragCoord.xy/iResolution.xy;

    //distance the ray travelled was saved in alpha channel
    float maxDist = texture(iChannel0,uv).a;

    vec2 texel = 1./iResolution.xy;
    vec4 total_color;
    for (int i=0;i<5;i++){
        float fi = float(i)-2.;
        for (int j=0;j<5;j++){ 
            float fj = float(j)-2.;
            vec4 color = texture(iChannel0, 
                uv + vec2( texel.x*fi,texel.y*fj )  );
            total_color += color * gk1s[i*5 + j];
        }
    }    

    fragColor = fwidth(total_color*total_color*1e5)*exp(-maxDist*maxDist*5.) ;

    //fragColor = sqrt(fragColor);

}

vec3 ifs_color, ro;
float ifs_scale = 1.2, maxDist, objId=0.;

mat2 rot(float a) {return mat2(cos(a),-sin(a),sin(a),cos(a));}

float de(vec3 z) {

    z.xz *= rot(iTime/7.);
    z.xy *=  rot(iTime/5.);

    vec3 min_vtx;
    vec3 orig_z = z;
    int n=0;
    float min_dist,dist_to_vtx;


    ifs_color = vec3(0.);

    for (int i=0; i<15; i++) {

        min_vtx = d12[0];
        min_dist=length(z-d12[0]);
        for (int j=1; j<20; j++) {

            dist_to_vtx=length(z-d12[j]); 
            if (dist_to_vtx<min_dist) {min_vtx=d12[j]; min_dist=dist_to_vtx;}

        }

        z = min_vtx + ifs_scale*(z-min_vtx);

        n++;

        if ( z.x * z.y > 0. ) ifs_color.x ++;
        if ( z.y * z.z > 0. ) ifs_color.y ++;
        if ( z.z * z.x > 0. ) ifs_color.z ++;

    }

    ifs_color /= float(n);

    float dz = pow(ifs_scale, float(n) );
    //dz is simply the constant Scale factor to the power of number of times used

    float scene_dist = length(z) / dz;  

    return scene_dist;
}


float march(vec3 ro, vec3 rd, float mx) {
    float t = 0.0;

    float eps = 5e-5, distfac=80.;
    float hitThreshold = eps;
    for(int i = 0; i < 30; i++) {
        float d = de(ro + rd*t);
        if ( (d < hitThreshold && t>.4) || t >= mx) break;
        t += d;
        hitThreshold = eps*(1.+distfac);

    }
    return t;
}

vec3 render(vec3 ro, vec3 rd) {
    float t = march(ro, rd, 5.);
    maxDist = t;


    return  cos(ifs_color)*cos(ifs_color) * exp(-t*t/2.) ;

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p  = (2.*fragCoord.xy-iResolution.xy)/iResolution.y*2.;

    ro = vec3(0.,0.,-.53);
    vec3 rd = normalize( vec3(p,2.) );  
    vec3 col = render(ro, rd);
    col = 1. - exp(-col);
    fragColor = vec4( pow(col,vec3(10.)), maxDist);

    //just saw this constrast trick in https://www.shadertoy.com/view/fttBDB by Dave Hoskins
    //makes the edges pop out  more
    //fragColor = fragColor*fragColor*(3.0-2.0*fragColor);

}
