#define PI 3.14159265359

mat2 rotate2D(float angle) {
    float s = sin(angle), c = cos(angle);
    return mat2(c, -s, s, c);
}

float rectangle(vec2 uv, vec2 size, float angle) {
    uv *= rotate2D(angle);
    vec2 d = abs(uv) - size;
    return length(max(d, 0.0)) + min(max(d.x, d.y), 0.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    vec3 color = vec3(0.0); // Background color (black)
    vec3 orangeColor = vec3(1.000,0.302,0.180); // Bright orange color

    float baseRectWidth = 0.2;
    float baseRectHeight = 0.13;
    float growthBase = 1.15; // Base for exponential growth

    float angleStep = PI / -7.5;
    float strokeWidth = 0.0015; // Width of the orange stroke

    float rotationSpeed = -1.5; // Adjust this to change rotation speed

    // Calculate the ping-pong factor based on time
    float pingPongFactor = abs(fract(iTime * 0.20) * 2.0 - 1.0);
    pingPongFactor = smoothstep(0.0, 1.0, pingPongFactor); // Smooth the transition

    // Initial zoom factor (adjust this to control how zoomed-in it starts)
    float minZoom = 1.0;
    float maxZoom = 5.0;

    for (int i = 30; i >= 0; i--) {
        float baseAngle = float(i) * angleStep;
        float rotationAngle = iTime * rotationSpeed; // Time-based rotation
        float angle = baseAngle + rotationAngle;

        // Calculate exponential growth
        float growthFactor = pow(growthBase, float(i)) - 1.0;

        // Calculate the size multiplier
        float sizeMultiplier = mix(minZoom + growthFactor, maxZoom + growthFactor, pingPongFactor);

        vec2 rectSize = vec2(baseRectWidth, baseRectHeight) * sizeMultiplier;

        float rect = rectangle(uv, rectSize, angle);
        float stroke = rectangle(uv, rectSize + strokeWidth, angle);

        vec3 rectColor = (i % 2 == 0) ? vec3(1.0) : orangeColor;
        color = mix(color, orangeColor, smoothstep(strokeWidth, strokeWidth - 0.001, stroke)); // Orange stroke
        color = mix(color, rectColor, smoothstep(0.001, 0.0, rect)); // Rectangle fill
    }

    fragColor = vec4(color, 1.0);
}
