void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 relative = fragCoord.xy + vec2(0, iResolution.y * - iScrolled);

    vec2 p = (relative/iResolution.y) * 2.0 - 1.0;

    vec3 xyz = vec3(p, 0);

    vec2 relativePos = ((xyz.xy/2.0) + vec2(iTime*iAnimationSpeed, 0));

    float n = (color(relativePos) + (sin(iTime/iColorSpeed)*(iColorAmount-1.0)))/iColorAmount;

    float lightness = .125;

    float saturation = 1.0;

    if (iDarkMode) {
        saturation = .5;
    }

    vec3 color = hsl2rgb(vec3((n*360.0)/360.0, saturation, lightness));

    if (iLightMode) {
        color = vec3(1, 1, 1) - color;
    }

    fragColor.xyz = color;
}
