// from https://shadertoy.com/view/Mf2BDG
// from https://shadertoy.com/view/lc2BDG
// from colored faces https://shadertoy.com/view/lcSfWG
// from base https://shadertoy.com/view/XfSfDy

#define rot(a)  mat2(cos(a+vec4(0,11,33,0)))               // rotation 
#define hue(v)  ( .6 + .6 * cos( 6.3*(v)  + vec4(0,23,21,0)  ) )

void mainImage(out vec4 O, vec2 U)
{
    float t=9., a,f,d,l,c, s=.628, w=radians(116.565);
    vec3  R = iResolution, 
          A = vec3( sin(w),0, cos(w) ),                   // North hemisphere axis (1st pie)
          B = vec3( sin(w)*cos(s),sin(w)*sin(s), -cos(w) ), // South hemisphere axis (1st pie)
          D = normalize(vec3(U+U, -8.*R.y) - R ),          // ray direction
          p = vec3(0,0,15), q,q0,                             // marching point along ray 
          M = iMouse.z > 0. ? iMouse.xyz/R -.5 :           // comment for no mouse cam
                 vec3( .15,.07,0) * cos(.3*iTime + vec3(0,11,0)); 

    for ( O=vec4(1); O.x > 0. && t > .01; O-=.01 )         // --- march scene
        q = p,
        q.yz *= rot(-6.*M.y),                              // rotations
        q.xz *= rot(-6.*M.x-1.57),
        q0=q,
        a = atan(q.y,q.x),                                 // 5-pie symmetry
        f =  mod( a+s,s+s ) - s,
        q.xy = length(q.xy)*cos( f +vec2(0,11) ),          // fold to 1st pie

        c = sign(q.z), f = l = length(q.xy),               // dist to pole
        d = length(q-A), d<l ? l = d, c = -round(1.+fract(a/6.28)*5.) : c, // dist to North hemisphere axes
        d = min( length(q-B),length(q-B*vec3(1,-1,1)) ),   // dist to South hemisphere axes // why 2 lengthes ? // length(p-A)) 
        d<l ? l = d, c = round(1.+fract((a+5.*s)/6.28)*5.) : c,  // â†’ c = face id, l = min dist to axis

        c > 0. ? q.xy *= rot(s*sign(q.y)) :U,              // polar parameterisation of faces
        f == l ? q.xy = q0.xy *sign(c)*vec2(1,-1)*rot(s)
               : q.xz *= rot(w*sign(c)),
        a = atan(q.y,q.x*sign(c)) + cos(2.6*l) + (c<0.?.03:.68),   // torsion cf https://www.facebook.com/groups/tiling/posts/1896835190740017/?comment_id=1896853150738221
    //  a = atan(q.y,q.x*sign(c)) + sin(2.*2.6*l)+ (c<0.?.03:.68), // variant

        t = length(q)- 1.2 + .1*cos(a*5.) + .0*cos(4.3*l), // face displacement

        p += .5*t*D;                                       // step forward = dist to obj   
   O *= O * 1.2;                                           // color scheme   
// t = sin(50.*a)*sin(80.*l); O *= .9+.1*smoothstep(0.,1.,abs(t)/min(.5,fwidth(t)));  // mesh grid
// O *= hue (a/6.28);                                      // debug: face polar coloring
// O *= hue (round(a/6.3*5.)/5.);                          // debug: patches coloring
// O = vec4(abs(c)/5., c>0., c<0., 0);                     // debug: faceId    
// O = mix( O, f==l ? vec4(0) : hue( c/5. ), smoothstep(1.,-1.,(l-.2)/fwidth(l)) ); // color discs
}
