// inspired by 
// https://www.facebook.com/groups/mcescher/posts/10160241063682197
// https://www.facebook.com/groups/tiling/posts/1880790585677811


// see readable version at bottom

void mainImage( out vec4 O, vec2 u )
{
    vec2  R = iResolution.xy,
          U = u - .5*R; 
   U = vec2( log(3.*length(U)/R.y/exp2(iTime) ),               // polar coordinates + scaling + sym
             .47* atan(U.y,abs(U.x)) ); 

   float d = .2645, s = sign(U.y+1e-5), e = s < 0. ? 0. : d;
   U.x = mod( U.x +d-e, d+d ) -d-e;                            // repetition
   d = length( ( U + vec2( e , -.3*s ) ) / vec2(.2,.4) ) - 1.; // base pattern
   O = vec4( .5 + s*d / fwidth(s*d) );                         // AA-draw
}





/** // --- 311 chars

#define C(P,R)   (length((P)/R)-1.) // ellipse quarter

void mainImage( out vec4 O, vec2 u )
{
    vec2  R = iResolution.xy,
          U = 1.5*( u+u - R ) / R.y;                          // normalized coordinates 
     U.x = abs(U.x);                                          // horizontal symmetry

   U = vec2(log(length(U)/ exp2(iTime) ), .47*atan(U.y,U.x)); // polar coordinates + scaling
                                                              // comment to see the base pattern
   float l, d = .2645, s = sign(U.y);

   U.x = s<0. ? mod(U.x-d, d+d)-d                             // repetition
              : mod(U.x  , d+d)-d-d;
   l = C( U + vec2( s<0. ?   0. : d , -.3*s ) ,vec2(.2,.4) ) * s; // base pattern

   O = vec4( .5 + l / fwidth(l) );                            // AA-draw
}

