float f_n_sd_ring( in vec2 p, float n_ang_nor, in float r, in float th )
{
    float n_tau = 6.2831;
    float n_radians = ((n_tau)*(n_ang_nor-.5)*.5);
    float n2 = n_tau-n_tau*(n_ang_nor/2.)+n_tau/2.;
    p = p*mat2(cos(n2), -sin(n2), sin(n2), cos(n2));
    vec2 n = vec2(sin(n_radians), cos(n_radians)); 
    p.x = abs(p.x);

    p = mat2(n.x,n.y,-n.y,n.x)*p;

    return max( abs(length(p)-r)-th*0.5,
                length(vec2(p.x,max(0.0,abs(r-p.y)-th*0.5)))*sign(p.x) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 o_trn = (fragCoord.xy-iResolution.xy*.5)/min(iResolution.y, iResolution.x);
    o_trn*=2.;
    float n_tau = 6.2831;

    float n_ang_nor = fract(atan(o_trn.x, o_trn.y)/n_tau);
    float n_sec = iDate.w;
    float n_sec_mod = mod(n_sec , 60.);
    float n_sec_mod_nor = n_sec_mod / 60.;
    float n_sec_mod_nor_snp = floor(n_sec_mod) / 60.;
    float n_min_mod = floor(mod(n_sec, 60.*60.));
    float n_min_mod_nor = n_min_mod / (60.*60.);
    float n_min_mod_nor_snp = (floor(n_min_mod_nor*12.))/12.;
    float n_hou_mod = floor(mod(n_sec, 60.*60.*12.));
    float n_hou_mod_nor = n_hou_mod / (60.*60.*12.);
    float n_hou_mod_nor_snp = (floor(n_hou_mod_nor*12.))/12.;

    float n_aa = (1./(iResolution.x+iResolution.y)/2.)*8.;
    float n_its = 3.;
    float n_it_nor_one = 1./n_its;

    vec4 o_col = vec4(0.);
    float n_min = 1.;
    float nx = clamp(o_trn.x, 0., .99);
    float n_x_12 = floor(nx*12.)/12.;
    float n_x_60 = floor(nx*60.)/60.;

    for(float n_it_nor = 0.; n_it_nor < 1.; n_it_nor+=n_it_nor_one){
        float n_radius_start = n_it_nor;
        float n_radius_end = n_it_nor+n_it_nor_one;
        float[] anmt = float[] (
            n_hou_mod_nor_snp, 
            n_min_mod_nor_snp, 
            n_sec_mod_nor_snp
        );
        float nmt = anmt[int((1.-n_it_nor)*3.)];
        float n = f_n_sd_ring(
            o_trn,
            1.-nmt,
            n_it_nor,
            (1./3.)-(1./12.)
        );
        n = abs(n);
        n = 1.-pow(n, 1./9.);
        //n = sin(n*20.);
        o_col += vec4(n);
    }
    float n = abs(fract(fract(n_ang_nor+(1./12./2.))*12.)-.5);
    n = 1.-pow(n, 1./3.);
    n = clamp(n, 0., 1.);
    n *= abs(length(o_trn)-.5);

    fragColor = vec4(max(o_col.x,n));
}
