const float pi = 3.1415926;
#define time iTime * 0.2

//hash by IQ https://www.shadertoy.com/view/XlXcW4
const uint k = 1103515245U;
vec3 hash( uvec3 x )
{
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;

    return vec3(x)*(1.0/float(0xffffffffU));
}


float noise(float t, int p){
    float x = 0.;

    for (int i = 0; i++<p;){
        vec3 h = hash(uvec3(i, 2<<i, 5*i));
        x += sin(h.x * t * float(i) + h.y*pi*2.) * h.z / float(i<<1);
    }

    return x;
}

void mainImage( out vec4 O, vec2 U ){
    vec2 r = iResolution.xy;
    vec2 uv = 2.*(2.*U-r)/r.y;
    vec2 auv = uv;

    float t = atan(uv.y,uv.x); //Thanks fad
    //if (uv.y==0.) t=0.;
    float x;


    if (length(uv) > 1.){

        float d = noise(t*2., 2<<6)*2.+1.1;
        d *= 1./(1.+pow(abs(uv.y), 2.));
        x = 0.1*d/((length(uv)-1.));

    } else {
        x = (1.-length(uv))*5e2;
        x = 1./sqrt(x);
    }


    uv.x += 
    //*
        abs(uv.y)>=1.?1e3:sqrt(1. - pow(abs(uv.y), 2.));
    /*/
        1.;
    //*/

    if (uv.y == 0.) uv.y = 1./r.y;
    x += 0.5*(sin(time)+1.)/(abs(uv.x)*abs(uv.y)) / ((abs(500.*uv.y)+1.)*(abs(200.*uv.x)+2.));

    O = mix(
        mix(
            vec4(
                sqrt(x), 
                x, 
                x*x, 
                1
            ), 
            vec4(
                x*x,
                x,
                sqrt(x),
                1
            ), 
            smoothstep(1., 2.5, length(auv))
        ),
        vec4(x),
    0.8);

    O*= 2.;
    O = O/(O+vec4(1));

}
