#define S(x) 3. * x * x - 2. * x * x * x
#define T iTime * .2

vec2 N22(vec2 st)
{
    st = vec2( dot(st,vec2(127.1,311.7)),
              dot(st,vec2(269.5,183.3)) );
    return -1.0 + 2.0*fract(sin(st)*43758.5453123);
}

float PerlinNoise(vec2 st)
{
    vec2 gv = fract(st);
    vec2 id = floor(st);
    vec2 bl = N22(id + vec2(.0, .0));
    vec2 br = N22(id + vec2(1., .0));
    vec2 tl = N22(id + vec2(.0, 1.));
    vec2 tr = N22(id + vec2(1., 1.));
    vec2 uv = S(gv);
    float b = mix(dot(bl, gv - vec2(.0, .0)), dot(br, gv - vec2(1., .0)), uv.x);
    float t = mix(dot(tl, gv - vec2(.0, 1.0)),dot(tr, gv - vec2(1., 1.)), uv.x);
    float n = mix(b, t, uv.y);
    return n * .5 + .5;
}

float FBM(vec2 uv, float layers)
{
    float freq = 1.;
    float am   = 1.;
    float n    = .0;
    float divide = .0;
    for(float i = 0.; i < layers; i++)
    {
        n += PerlinNoise(uv * freq) * am;
        divide += am;
        freq *= 2.;
        am   *=  .5;
    }
    return n / divide;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.y;
    uv += vec2(T);
    vec3 col = vec3(0.);
    float n = FBM(uv * 5., 5.0);
    col = vec3(n);
    fragColor = vec4(col, 1.0);
}
