    05/12/2023  @byt3_m3chanic

    Reworking, and adding some neon glow, different textures and
    optimized code based on what I picked up from @Shane's shaders.
    Simple one pass hexagon grid and simple get sdf face for texturing.

*/

#define R iResolution
#define T iTime
#define M iMouse

#define PI          3.14159265
#define PI2         6.28318530

#define MIN_DIST    1e-4
#define MAX_DIST    20.


// globals
vec4 FC = vec4(0.059,0.078,0.141,0);
vec3 hp,hit;
vec2 gid, sid, shx, ghx;
mat2 r2;
float speed, glow = 0.,sglow = 0.,shs,ghs;

// constants
const float thrs = .75;
const float s4 = .577350;
const float s3 = .288683;
const float s2 = .866025;
const vec2 s = vec2(1.732,1);

float hash21(vec2 p) { return fract(sin(dot(p,vec2(26.37,45.93)))*4374.23); }
mat2 rot(float a){ return mat2(cos(a),sin(a),-sin(a),cos(a)); }
vec3 hue(float t) { return .45 + .35*cos(PI2*t*(vec3(1.,.85,.75)+vec3(0.941,0.667,0.278)));}

// @IQ extrude 2D sdf
float opx(in float d, in float z, in float h){
    vec2 w = vec2( d, abs(z) - h );
  	return min(max(w.x, w.y), 0.) + length(max(w, 0.));
}

// @Shane tile grid and functions
// https://www.shadertoy.com/view/4td3zj
vec4 hexGrid(vec2 uv) {
    vec4 hC = floor(vec4(uv, uv - vec2(1,.5))/s.xyxy) + .5;
    vec4 h4 = vec4(uv - hC.xy*s, uv - (hC.zw + .5)*s);
    return dot(h4.xy, h4.xy) < dot(h4.zw, h4.zw) ? vec4(h4.xy, hC.xy) : vec4(h4.zw, hC.zw + .5);
}

vec2 map(vec3 pos) {
    vec2 res = vec2(1e5,0);
    pos.x += speed;

    vec2 uv = pos.xz;
    vec4 hex = hexGrid(uv/2.);
    vec2 id = hex.zw, p = hex.xy;

    sid = id, shx = p;

    float rnd = hash21(id);

    if(rnd<.5) {
        p *= r2;
        p.y = -p.y;
    } 

    vec2 p0 = p - vec2(-s3, .5),
         p1 = p - vec2( s4,  0),
         p2 = p - vec2(-s3,-.5);

    vec3 d3 = vec3(length(p0), length(p1), length(p2));
    vec2 pp = vec2(0);

    if(d3.x>d3.y) pp = p1;
    if(d3.y>d3.z) pp = p2;
    if(d3.z>d3.x && d3.y>d3.x) pp = p0;

    float circle = length(pp)-s3;

    rnd = fract(rnd*4334.343);
    if(rnd<.175) {    
        float c1 = 1e5, c2 = 1e5;
        circle = length(p.x);
        c2 = length(p1)-s3;
        c1 = length(p1+vec2(1.155,0))-s3;
        c1 = min(c2,c1);
        circle = min(circle,c1);
    }

    ghs = rnd;

    float ws = .5+.4*sin(pos.x*.6);
    float pat = opx(abs(circle)-.025,pos.y-.2-ws,.15);
    float tile = max(abs(hex.x)*s2 + abs(hex.y)*.5, abs(hex.y))-.485;

    float glw = opx(abs(tile+.2)-.005,pos.y-.2,.01)-.001;

    if(rnd>thrs) glow+=.00075/(.0025+glw*glw);
    if(glw<res.x&&rnd>thrs) res=vec2(glw,2.);

    pat = max(pat,opx(tile,pos.y,2.));
    if(pat<res.x) {
        res=vec2(pat,1.);
        hit=vec3(pp.x,pos.y,pp.y);
    }

    tile = max(abs(hex.x)*s2 + abs(hex.y)*.5, abs(hex.y))-.45;
    float flr = min(opx(tile,pos.y,.05),pos.y+.01);

    if(flr<res.x) {
        res=vec2(flr,3.);
        hit=pos;
    }

    return res;
}

vec3 normal(vec3 p, float t) {
    float e = MIN_DIST*t;
    vec2 h =vec2(1,-1)*.5773;
    vec3 n = h.xyy * map(p+h.xyy*e).x+
             h.yyx * map(p+h.yyx*e).x+
             h.yxy * map(p+h.yxy*e).x+
             h.xxx * map(p+h.xxx*e).x;
    return normalize(n);
}

// @Shane based on the original by @IQ.
float calcAO(in vec3 p, in vec3 n) {
	float sca = 4., occ = 0.;
    for( int i=1; i<5; i++ ) {
        float hr = float(i)*.125/5.;        
        float dd = map(p + hr*n).x;
        occ += (hr - dd)*sca;
        sca *= .8;
    }
    return clamp(1.-occ,0.,1.);   
}

vec4 render(inout vec3 ro, inout vec3 rd, inout vec3 ref, inout float d) {

    vec3 C = FC.rgb;
    float m = 0.;
    vec3 p = ro;

    for(int i=0;i<90;i++) {
        p = ro + rd * d;
        vec2 ray = map(p);
        if(ray.x<MIN_DIST*d||d>MAX_DIST)break;
        d += i<28?ray.x*.5:ray.x;
        m  = ray.y;
    } 

    gid=sid, ghx=shx, hp=hit;

    if(d<MAX_DIST)
    {
        vec3 n = normal(p,d);
        vec3 lpos =  vec3(2,18,-2);
        vec3 l = normalize(lpos-p);

        float diff = clamp(dot(n,l),0.,1.);

        int face;
        vec3 cuv = hp;

        // quick find the face with normal
        if(m==1.) {
            vec3 aN = abs(n);
            ivec3 idF = ivec3(n.x<-.25? 0 : 5, n.y<-.25? 1 : 4, n.z<-.25? 2 : 3);
            face = aN.x>.5? idF.x : aN.y>.5? idF.y : idF.z;

            if(face==0||face==5) cuv = hp.yzx;
            if(face==1||face==4) cuv = hp.xzy;
            if(face==2||face==3) cuv = hp.xyz;

        }

        float shdw = 1.;
        for( float t=.01;t<10.; ) {
            float h = map(p + l*t).x;
            if( h<MIN_DIST ) { shdw = 0.; break; }
            shdw = min(shdw, 32.*h/t);
            t += h;
            if( shdw<MIN_DIST || t>16. ) break;
        }

        float ao = calcAO(p, n);
        diff = mix(diff,diff*shdw,.65)*ao;

        float spec = .75 * pow(max(dot(normalize(p-ro),reflect(normalize(lpos),n)),0.),24.);
        vec3 h = vec3(.5);
        if(m==1.) {
            h = texture(iChannel1,cuv.xy).rgb;
            ref = h;
        }
        if(m==3.) {
            float px = fwidth(p.x);
            float hs = hash21(gid);
            hp.xz *= rot(hs*PI2);

            float tile = max(abs(ghx.x)*s2 + abs(ghx.y)/2., abs(ghx.y))-.465;
            float cell = tile;
            vec4 hex = hexGrid(hp.xz*4.);
            vec2 id = hex.zw, pq = hex.xy;

            float rnd = hash21(id);
            if(rnd<.5) {
                pq *= r2;
                pq.y = -pq.y;
            } 

            float circle;
            if(fract(hs*43.234)>.55) {
                vec2 p0 = pq - vec2(-s3,.5), p1 = pq - vec2(s4, 0), p2 = pq - vec2(-s3,-.5);
                vec3 d3 = vec3(length(p0),length(p1),length(p2));
                vec2 pp = vec2(0);

                if(d3.x>d3.y) pp = p1;
                if(d3.y>d3.z) pp = p2;
                if(d3.z>d3.x && d3.y>d3.x) pp = p0;
                circle = length(pp)-s3;
                tile = max(tile,max(cell+.025,abs(abs(abs(circle)-.15)-.075)-.025));
            }

            h = mix(texture(iChannel2,hp.xz*3.).rgb,texture(iChannel0,hp.xz*.5).rgb*vec3(0.827,0.647,0.392),smoothstep(px,-px,cell));
            h = mix(h,h.b*hue(hs/3.),smoothstep(px,-px,tile));
            h = mix(h,h*.05,smoothstep(px,-px,abs(cell+.015)-.01));

            ref = vec3(.6);
        }

        C = h * diff;

        ro = p+n*.001;
        rd = reflect(rd,n);
    } 

    return vec4(C,1.);
}

void mainImage( out vec4 O, in vec2 F ) {

    r2 = rot(1.047197);
    speed = T*.35;
    vec2 uv = (2.*F.xy-R.xy)/max(R.x,R.y);

    vec3 ro = vec3(0,0,6);
    vec3 rd = normalize(vec3(uv, -1));

    // mouse //
    float x = M.xy==vec2(0) || M.z<0. ? 0. : -(M.y/R.y*.25-.125)*PI;
    float y = M.xy==vec2(0) || M.z<0. ? 0. : -(M.x/R.x*2.-1.)*PI;
    mat2 rx =rot(-1.1-x), ry =rot(-.78-y-T*.01);

    ro.zy*=rx, ro.xz*=ry;
    rd.zy*=rx, rd.xz*=ry;

    vec3 C = vec3(0), ref = vec3(0), fil = vec3(1);

    float d = 0.,a = 0.;
    float bounces = 2.;

    for(float i=0.; i<bounces; i++) {
        d =0.;
        vec4 pass = render(ro, rd, ref, d);
        C += pass.rgb*fil;
        fil*=ref;
        sglow = glow;
        if(i==0.) {a = d, shs = ghs; };
    }
    vec3 clr = fract(shs*3434.34)>.5?vec3(sglow*.15,sglow*.65,sglow):vec3(sglow,sglow*.65,sglow*.15);
    C = mix(C,clr,clamp(sglow,.0,.5));
    C = mix(FC.rgb,C,  exp(-.0018*a*a*a));
    C = pow(C, vec3(.4545));
    O = vec4(C,1);
}
