// Author Spencer Yonce - 2024
// Title: Metaballs
#define t iTime
#define res iResolution

void mainImage(out vec4 c, in vec2 fc){
	vec2 st = fc/res.xy;
    float pct = 0.0;
    pct = (
        	distance(st,vec2(0.4,0.4)+vec2(sin(t)*0.2,cos(t))*0.2)*
            distance(st,vec2(0.750,0.310)+vec2(cos(t*0.314)*0.02,sin(t)*0.05))*
            distance(st,vec2(0.610,0.590))*
        	distance(st,vec2(0.300,0.830))*
        	distance(st,vec2(0.230,0.550)+vec2(sin(t*1.1)*0.2,(cos(t*1.24)*0.24)))
          );

    vec3 color = vec3(0.0);
    color = (1.0-vec3(step(0.002,pct)))*vec3(st.xyy);
	c = vec4( color, 1.0 );
}
