#define sat(x) clamp(x,0.0,1.0)

float hash(vec3 p){
	p  = fract(p*0.3183099+0.1) * 17.0;
	return fract(p.x*p.y*p.z*(p.x+p.y+p.z));
	//return fract(cos(2932.9286*sin(dot(sin(p*2938.2982),vec3(2937.9282,8279.1178,1182.3382)))));
}

float onoise(vec3 x){
	vec3 i = floor(x), f = fract(x);
	f = f*f*(3.0-2.0*f);
	return mix(mix(mix(hash(i),hash(i+vec3(1,0,0)),f.x),
		mix(hash(i+vec3(0,1,0)),hash(i+vec3(1,1,0)),f.x),f.y),
		mix(mix(hash(i+vec3(0,0,1)),hash(i+vec3(1,0,1)),f.x),
		mix(hash(i+vec3(0,1,1)),hash(i+vec3(1,1,1)),f.x),f.y),f.z);
}

float fbm(vec3 p,float m){
	float f = 0.0;
	f += 0.5000*onoise(p); p = m*p*2.01;
	f += 0.2500*onoise(p); p = m*p*2.02;
	f += 0.1250*onoise(p); p = m*p*2.03;
	f += 0.0625*onoise(p); p = m*p*2.01;
	return f;
}

float tnoise(vec3 x){
	return texture(iChannel0,0.025*x).r;
}

float tfbm(vec3 p,float m){
	float f = 0.0;
	f += 0.5000*tnoise(p); p = m*p*2.01;
	f += 0.2500*tnoise(p); p = m*p*2.02;
	f += 0.1250*tnoise(p); p = m*p*2.03;
	f += 0.0625*tnoise(p); p = m*p*2.01;
	return f;
}

void mainImage(out vec4 fragColor,vec2 fragCoord){
	vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;
	vec3 col = vec3(0.0);

	vec2 p = vec2(uv.x/max(0.0,-uv.y+0.5),1.0/max(0.0,-uv.y+0.5)-2.5)*0.25;

	if(uv.y < 0.5){
		float t = tfbm(10.0*vec3(p+iTime*0.2,0),1.0)-0.45;
		col += 0.1+0.9*mix(vec3(0.1,0.2,0.6)*floor(sat(0.01/t+0.5)*4.0)/4.0,mix(vec3(0.85,0.6,0.35),vec3(0.2,0.8,0.3)*floor((1.0-(0.01/-t-0.2))*3.0+0.07)/2.0,step(t,-0.018)),step(t,0.0));
		float t3 = tfbm(5.0*vec3(p+vec2(0,0.085)+100.0+iTime*0.05+vec2(sin(iTime*0.035),cos(iTime*0.035))*3.0,iTime*0.01),1.3)*4.0-1.5;
		col = mix(col,col-vec3(0.2),sat(floor(t3*2.0)/2.0));
		float t2 = tfbm(5.0*vec3(p+100.0+iTime*0.05+vec2(sin(iTime*0.035),cos(iTime*0.035))*3.0,iTime*0.01),1.3)*4.0-1.5;
		col = mix(col,vec3(1),sat(floor(t2*2.0)/2.0));
	}

	col += vec3(0,1,3)*(0.008/abs(length(uv.y-0.5)));

	col += vec3(0,1,3)*(1.0-exp(uv.y*-0.1));
	col *= 1.0-length(fragCoord/iResolution.xy-0.5);

    fragColor = vec4(col,1.0);
}
