// Fork of "iterative normalization" by janpitokiala. https://shadertoy.com/view/McfBRr
// 2024-08-10 15:14:52

#define DARK

#ifdef DARK
#define BACKGROUND vec3(.05)
#define MAIN_COLOR vec3(1.)
#else
#define BACKGROUND vec3(1.)
#define MAIN_COLOR vec3(0.)
#endif
#define FADED_COLOR mix(BACKGROUND,MAIN_COLOR,.3)

#define CIRCLE_WIDTH .007
#define CIRCLE_SIZE .8
#define POINT_SIZE .04
#define FADED_POINT_SIZE .02

#define ITERATIONS 1
#define TAYLOR_ITERATIONS 10.
float astepDeriv(float edge, float x, float deriv) {
    return clamp((x-edge)/deriv+.5,0.,1.);
}
float invsqrt(float x) {
  float mul = 1.;
  float sum = 1.;
  for(float i=1.;i<=TAYLOR_ITERATIONS;i++) {
    mul *= -(2.*i-1.)*x/(2.*i);
    sum += mul;
  }
  return sum;
}
vec2 normalizeStep(vec2 x) {
    return x*invsqrt(dot(x,x)-1.);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord*2./iResolution.y+vec2(-iResolution.x/iResolution.y,-1.);
    float uvDeriv = 2./iResolution.y;
    vec2 m = iMouse.xy*2./iResolution.y+vec2(-iResolution.x/iResolution.y,-1.);
    if(iMouse.x==.0&&iMouse.y==.0) { //demo
      m=sin(iTime*vec2(1.3,1.5));
    }
    vec3 col = BACKGROUND;
    col = mix(col, MAIN_COLOR, astepDeriv(0.,CIRCLE_WIDTH-abs(length(uv)-CIRCLE_SIZE),uvDeriv));
    vec2 point = m/CIRCLE_SIZE;
    for(int i=0;i<ITERATIONS;i++) {
      col = mix(col, FADED_COLOR, astepDeriv(0.,FADED_POINT_SIZE-length(uv-point*CIRCLE_SIZE),uvDeriv));
      point = normalizeStep(point);
    }
    col = mix(col, MAIN_COLOR, astepDeriv(0.,POINT_SIZE-length(uv-point*CIRCLE_SIZE),uvDeriv));

    // Output to screen
    fragColor = vec4(col,1.0);
}
