bool is_prime(int n) {
    if(n==2) return true;
    if(n < 2 || n % 2 == 0) return false;
    for(int i = 3; i <= int(sqrt(float(n))); i++) {
        if(n % i == 0) return false;
    }
    return true;
}


float hash(float n) {
    return fract(sin(n) * 43758.5453123);
}

vec3 hash31(float n) {
    float h1 = hash(n);
    float h2 = hash(h1 * 97.1);  
    float h3 = hash(h2 * 43.7);
    return vec3(h1, h2, h3);
}

int totient(int n) {
    int result = n;
    for (int i = 2; i * i <= n; i++) {
        if (n % i == 0) {
            while (n % i == 0) n /= i;
            result -= result / i;
        }
    }
    if (n > 1) result -= result / n;
    return result;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;
    uv *= 200.;
    uv += iTime*20.;

    int x = int(uv.x);
    int y = int(uv.y);

    int q = (x | y);
    //q = 1+totient(q);

    vec3 h = hash31(float(q));

    vec3 col = vec3(0.);
    if(is_prime(q)) {
        col += h;
    }

    fragColor = vec4(col,1.0);
}
