float se(vec2 p, float k, float r) {     
    return pow(abs(p.x), k) + pow(abs(p.y), k) - r;    
}

vec3 pal(float t) {
    vec3 a = vec3(.5);    
    return a + a*cos(6.28*(1.*t+vec3(.26, .41, .55)));
}

void mainImage( out vec4 O, vec2 F ) {
    vec2 R = iResolution.xy,
         u = (F+F-R) / R.y*.6 
         + vec2(iTime*.1,0.),
         c;// = vec2(1.); // a fix for @bitshifter. Thx <3
    vec3 o;
    float s, T = iTime*.2;

    for(float i=0.; i<6.; i+=1.) {
		s = se(mod((u = fract(u * 1.2) - .5)+.5*c,c)-.5*c+i, .8, .64);
		s = smoothstep(.02,0.,
                abs(
                    sin(
                        (s+=(.02)*cos(3.*atan(u.x, u.y)*90.+T)
                     )
                  *4. - T)/8.)
             );		

		o += pal(length(u) + i*.02 + T)*s;
	}

    O = vec4(o,1);
}
