Main {
    Q = .5*B(U);
    vec4 n = B(U+vec2(0,1));
    vec4 e = B(U+vec2(1,0));
    vec4 s = B(U-vec2(0,1));
    vec4 w = B(U-vec2(1,0));
    vec2 g = vec2(e.y-w.y,n.y-s.y);
    Q *= .5+10.*g.x;
    U = 2.*(U-.5*R)/R.y;
    for (float i = 0.; i < N; i++) {

        vec4 q = A(vec2(i,0)+.5);
        if (length(U-q.xy)>.2) continue;
        float r = 1.-exp(-.1*i)+exp(.1*(i-N));
        vec2 u = (U-q.xy);

        q = texture(iChannel1,4.*(u*ei(q.z)*.5)+.5)*(1.+.5*(u.y)*30./(.1+r));
            Q  = mix(Q,q,smoothstep(.05*r+.01,.04*r,length(u)));


    }


}

void init(vec2 U, out vec4 Q0, vec4 dQ0) {
    float theta = 4.*2.*pi*U.x/N+iTime;
    Q0.xy = vec2(U.x/N*2.-1.,.25*sin(theta));

    Q0.z = sin(theta);
    dQ0.xy = vec2(0);
    dQ0.z = 0.;
}
Main {
    if (U.y > 2.) discard;
    vec4 Q0, dQ0;
    init(U,Q0,dQ0);
    Q =A(vec2(U.x,0.5));
    vec4 dQ = A(vec2(U.x,1.5));
    vec4 n = B((Q.xy*.5+.5)*R+vec2(0,1));
    vec4 e = B((Q.xy*.5+.5)*R+vec2(1,0));
    vec4 s = B((Q.xy*.5+.5)*R-vec2(0,1));
    vec4 w = B((Q.xy*.5+.5)*R-vec2(1,0));
    vec2 g = vec2(e.y-w.y,n.y-s.y);
    vec4 f = vec4(0);
    f.xy -= .001*g*abs(sin(4.*2.*pi*U.x/N+iTime));
    vec2 d = vec2(cos(Q.z),sin(Q.z));
    for (float i = 0.; i < N; i++) if (i!=floor(U.x)) {
        vec4 q = A(vec2(i+.5,0.5));
        vec4 q0, dq0;
        init(vec2(i+.5,0),q0,dq0);
        vec2 r = q.xy-Q.xy;
        float l = length(r);
        float L = length(q0.xy-Q0.xy);
        if (l>0.) {
            f.xy += 20.*exp(-32.*L)*r*(l-L)/l/N;
            f.z += 20.*dot(d.yx*vec2(-1,1),r)*exp(-12.*L)/N;
            f.xy -= r/l*(exp(-37.*l)-exp(-32.*L))/N;
        }
    }

    dQ += f;
    Q += f+dQ;


    if (Q.x < -R.x/R.y*.9) Q.x = -.9*R.x/R.y,dQ.x = abs(dQ.x); 
    if (Q.x > +R.x/R.y*.9) Q.x = +.9*R.x/R.y,dQ.x =-abs(dQ.x); 
    if (Q.y < -.9) Q.y = -.9,dQ.y = abs(dQ.y); 
    if (Q.y > +.9) Q.y = +.9,dQ.y =-abs(dQ.y); 

    if (iFrame < 1) {
        Q = Q0;
        dQ = dQ0;
    }

    if (U.y>1.) Q = dQ;

}
