#define PI 3.14159
#define R iResolution
#define dt iTime

const vec3
black=vec3(0.),
white=vec3(1.),
A=vec3(1.078, 0.500, 0.500),
B=vec3(0.248, 0.318, 0.500),
C=vec3(-0.622, 0.878, 0.618),
D=vec3(0.000, 0.333, 0.667);
vec3 palette(vec3 t){return A+B*cos(2.*PI*(C*t+D));}
vec3 palette(float t){return palette(vec3(t));}

mat2 rot2D(float a){float c=cos(a),s=sin(a);return mat2(c,-s,s,c);}

vec2 imul(vec2 a, vec2 b) {
    return vec2(
        a.x * b.x - a.y * b.y,
        a.x * b.y + a.y * b.x
    );
}

vec3 julia(vec2 uv) {
    vec2 z,c;
    float i,N,t;
    t=dt*.05;
    z=uv; c=vec2(pow(cos(t),3.), pow(sin(t),3.));
    N=100.;i=0.;
    for (;i<N;i++) {
        z=imul(z,z)+c;
        if (abs(dot(z,z))>4.) break;
    }
    if (i==N) return black;
    else return palette(i*10.);
}

void mainImage(out vec4 C, in vec2 I) {
    vec2 uv = (2.*I-R.xy)/R.y;
    uv*=1.2;
    vec3 col = julia(uv);
    C = vec4(col,1.0);
}
