#define SAMPLE_POINT_LOW vec2 (0.09, 0.2)
#define SAMPLE_POINT_MID vec2 (0.3, 0.1)
#define SAMPLE_POINT_HIGH vec2 (0.8, 0.2)

#define MODE 3

// === icon message: "sound in". link: https://www.shadertoy.com/view/MtyXRW

float message(vec2 uv) { // to alter in the icon
    uv-=vec2(1.,10.); if ((uv.x<0.)||(uv.x>=32.)||(uv.y<0.)||(uv.y>=3.)) return -1.; 
    int i=1, bit=int(pow(2.,floor(32.-uv.x)));
    if (int(uv.y)==2) i=  928473456/bit; // 00110111 01010111 01100001 01110000
    if (int(uv.y)==1) i=  626348112/bit; // 00100101 01010101 01010000 01010000
    if (int(uv.y)==0) i= 1735745872/bit; // 01100111 01110101 01100001 01010000
 	return float(i-2*(i/2));
}

float sampleMusic(vec2 p) {
    return normalize (texture (iChannel0, p)).r;
}

float getBass () {
    return sampleMusic(SAMPLE_POINT_LOW);   
}

float getMids() {
    return sampleMusic(SAMPLE_POINT_MID);
}

float getTreble () {
    return sampleMusic(SAMPLE_POINT_HIGH);   
}

vec4 createShape_ (float t, vec4 music) {
    vec4 treble = vec4(getTreble ());
    vec4 nt = normalize (treble);

    float m = mix (5., length (music), length (nt.xz));
    float n1 = 1. + 6. * music.z;
    //float n1 = mix (InvGold, mix (-50., -5., max (0.01, abs (sin (treble.x)))), abs (tan (music.z)));
    float n2 = 2. + 10. * music.z ;
    float n3 = mix (2., length (nt.yw), normalize (music).z);

    return vec4 (m, n1, n2, n3);
}

vec2 createSymmetry_ (float t) {
    vec4 music = vec4(getBass ());

    float x = 1.;
    float y = 1.;

    return vec2 (x, y) * music.z;
}

vec4 crazyNoise (vec4 music, vec2 fragCoord, float t) {    
    vec4 noise = vec4 (
        abs (tan (random (fragCoord + music.xy) + music.z)) + 0.1 * sin (t),
        abs (sin (random (fragCoord + music.yx) + music.z)) + 0.1 * sin (t),
        abs (tan (random (fragCoord + music.yx) + music.z)) + 0.1 * sin (t),
        1.
    );

    return noise;
}

void mainImage (out vec4 fragColor, in vec2 fragCoord) {
    if (iResolution.y<2000.) {float c=message(fragCoord.xy/8.);if(c>=0.){fragColor=vec4(c);return;}}  

    float radius = iResolution.y / 4.;

    vec2 center = iResolution.xy / 2.;
    vec2 d = fragCoord - center;
    vec2 up = vec2 (0., 1.);
    vec2 uv = fragCoord.xy / iResolution.xy;

    float t = iTime * 0.6;
    float tTrack = iChannelTime[0];
    float angle = angleBetween (up, d);
    vec4 music = vec4(
        7. * getTreble() + 2., //*getTreble() + .1,
        3. * getBass() + 2., // 10.*getBass(), //3. * getMids() + .2,
        2. * getMids() + 1., //getTreble() + .1, //5. * getTreble() + +.1,
        1.0
    );

    float symmetryBase = InvGold * getBass() + OneMinusInvGold * getTreble();
    vec2 symmetry = createSymmetry (8. * symmetryBase + 2.);
    vec4 shape = createShape_ (abs(cos(t+10.)) + 4., music);
    float s = superformula (angle, shape, symmetry);
    float r = s * radius;

    float crazyPattern = tan (angle * t + t);
    float k = tTrack;
    float searchLights = cos (angle + k) + sin (k) + sin (cos (angle + k));

    int mode = int(mod(t, 4.));
    switch (mode) {
        case(0):
            fragColor = vec4 (InvGold) * (0.01 * crazyPattern + 0.2 * searchLights);
            break;
        case(1):
            fragColor = cos (angle) * 0.01 * crazyPattern * vec4 (InvGold);
            break;
        case(2):
            fragColor = vec4 (OneMinusInvGoldSquared) * searchLights;
            break;
        case(3):
            fragColor = .3 - (crazyNoise (music, fragCoord, t) * searchLights);
            break;
    }

    if (length (d) < r) {
        float dr = r / length (d);

        vec4 c = normalize (shape);
        c.r = mix (1. - c.r, c.r, dr);
        c.g = mix (0., 1. - c.g, dr);
        c.b = 1. - c.b * dr;
        //c.a = mix (0.6, 1., dr);

        fragColor = InvGold * crazyPattern * c + OneMinusInvGold * dr * fragColor;
    }

    //fragColor = vec4(vec2(abs(sin(tTrack))), .0, 1.);
    //fragColor = vec4(vec3(angle), 1.);
    //fragColor = vec4(getBass());
}

const float PI = 355.0/113.0;
const float TWO_PI = 2.*PI;
const float Gold = (1.0+sqrt(5.0)) / 2.0;
const float InvGold = 1. / Gold;
const float OneMinusInvGold = 1.0 - InvGold;
const float OneMinusInvGoldSquared = OneMinusInvGold * OneMinusInvGold;

float random (vec2 co) {
   return fract (sin (dot (co.xy, vec2 (12.9898,78.233))) * 43758.5453);
}

float superformula (float angle, vec4 shape, vec2 symmetry) {    
    float m = shape.x;
    float n1 = shape.y;
    float n2 = shape.z;
    float n3 = shape.w;
    float a = symmetry.x;
    float b = symmetry.y;

    float rpart = (m * angle) / 4.;
    float apart = abs (cos (rpart) / a);
    float bpart = abs (sin (rpart) / b);

    float r = pow (
        pow (apart, n2) + pow (bpart, n3),
        -1. / n1
    );

    return r;
}

float angleBetween (vec2 a, vec2 b) {
    float cosTheta = dot (a, b) / (length (a) * length (b));    
    return acos (cosTheta);
}

vec2 createSymmetry (float t) {
    vec2 v = vec2 (.618 + sin (t + PI / 2.) * .5, .5 + cos (t) * .618);
    return v;
}

vec4 createShape (float t) {    
    float m = 1. + abs (sin (t) + cos (1.6 * t)) * 8.;
    float n1 = 1. + abs (sin (2. * t) * 4.);
    float n2 = 1. + (cos (t + PI / 1.6) * 2.);
    float n3 = 2. + sin (1.6 * t + PI) * 4.;

    return vec4 (m, n1, n2, n3);
}
