// "Bouncing Chain" by dr2 - 2018
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

#define AA  0   // optional antialiasing

float PrTorusDf (vec3 p, float ri, float rc);
vec2 Rot2D (vec2 q, float a);
mat3 QtToRMat (vec4 q);
vec3 HsvToRgb (vec3 c);
float Fbm2 (vec2 p);
vec4 Loadv4 (int idVar);

vec3 qHit, ltDir;
float dstFar, ringRad;
int idObj;
const int nObj = 16;
const int nSphObj = 18;
const float pi = 3.14159, sqrt3 = 1.73205;

float ObjDf (vec3 p)
{
  return PrTorusDf (p, 0.5, ringRad - 0.5);
}

float ObjRay (vec3 ro, vec3 rd)
{
  mat3 mRot;
  vec3 rm, rdm, q;
  float dHit, dMin, d, tol;
  tol = 0.0005;
  dMin = dstFar;
  for (int n = 0; n < nObj; n ++) {
    mRot = QtToRMat (Loadv4 (4 * n + 2));
    rm = (ro - Loadv4 (4 * n).xyz) * mRot;
    rdm = rd * mRot;
    dHit = 0.;
    for (int j = 0; j < 120; j ++) {
      q = rm + dHit * rdm;
      d = ObjDf (rm + dHit * rdm);
      dHit += d;
      if (d < tol || dHit > dstFar) break;
    }
    if (d < tol && dHit < dMin) {
      dMin = dHit;
      qHit = q;
      idObj = n;
    }
  }
  return dMin;
}

vec3 ObjNf (vec3 p)
{
  mat3 mRot;
  vec4 v;
  vec2 e = vec2 (0.0002, -0.0002);
  mRot = QtToRMat (Loadv4 (4 * idObj + 2));
  p = (p - Loadv4 (4 * idObj).xyz) * mRot;
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return mRot * normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  mat3 mRot;
  vec3 rm, rdm;
  float sh, d, h;
  sh = 1.;
  for (int n = 0; n < nObj; n ++) {
    mRot = QtToRMat (Loadv4 (4 * n + 2));
    rm = (ro - Loadv4 (4 * n).xyz) * mRot;
    rdm = rd * mRot;
    d = 0.02;
    for (int j = 0; j < 20; j ++) {
      h = ObjDf (rm + d * rdm);
      sh = min (sh, smoothstep (0., 0.05 * d, h));
      d += clamp (3. * h, 0.1, 1.);
      if (sh < 0.05) break;
    }
    if (sh < 0.05) break;
  }
  return 0.5 + 0.5 * sh;
}

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

float HexEdgeDist (vec2 p)
{
  p = abs (p);
  return (sqrt3/2.) - p.x + 0.5 * min (p.x - sqrt3 * p.y, 0.);
}

vec3 BgCol (vec3 ro, vec3 rd, float sh)
{
  vec3 vn, col, bgCol;
  bgCol = vec3 (0.3, 0.2, 0.1);
  if (rd.y < 0.) {
    ro -= (ro.y / rd.y) * rd;
    col = mix (vec3 (0.4, 0.3, 0.3), bgCol, Fbm2 (8. * ro.xz));
    vn = vec3 (0., 1., 0.);
    col *= 0.3 + 0.7 * sh * max (dot (vn, ltDir), 0.);
    col = mix (col, bgCol, min (0.003 * length (ro.xz), 1.));
  } else col = bgCol;
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 vn, col;
  vec2 p, ip;
  float dstFlr, dstObj, dEdge, c, sh;
  bool isBg;
  ringRad = Loadv4 (4 * nObj).z;
  dstFlr = (rd.y < 0.) ? - (ro.y - 0.5) / rd.y : dstFar;
  dstObj = ObjRay (ro, rd);
  isBg = false;
  sh = 1.;
  if (min (dstObj, dstFlr) < dstFar) {
    if (dstFlr < dstObj) {
      ro += rd * dstFlr;
      vn = vec3 (0., 1., 0.);
      isBg = true;
    } else {
      ro += rd * dstObj;
      vn = ObjNf (ro);
      p = vec2 (atan (qHit.z, ringRad - 0.5 - length (qHit.xy)),
         atan (qHit.y, - qHit.x)) / pi + 1.;
      p *= 6. * vec2 (sqrt3/2., (ringRad - 0.5) / 0.5);
      ip = PixToHex (p);
      dEdge = HexEdgeDist (p - HexToPix (ip));
      c = mod (dot (mod (2. * ip + ip.yx, 3.), vec2 (1., 2.)), 3.);
      col4 = vec4 (HsvToRgb (vec3 (mod (1.61803 * float (idObj) + 0.1 * c, 1.), 1., 1.)), 0.3);
      col4 = mix (vec4 (0.1), col4, smoothstep (0.05, 0.1, dEdge));
    }
    sh = ObjSShadow (ro, ltDir);
  } else isBg = true;
  if (! isBg) col = col4.rgb * (0.2 + 0.1 * max (dot (normalize (- ltDir.xz), vn.xz), 0.) +
     0.8 * sh * max (dot (vn, ltDir), 0.)) +
     col4.a * sh * pow (max (dot (normalize (ltDir - rd), vn), 0.), 32.);
  else col = BgCol (ro, rd, sh);
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec4 mPtr;
  vec3 col, rd, ro, rMid, vd;
  vec2 canvas, uv, uvv;
  float az, el, zmFac;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  dstFar = 100.;
  rMid = vec3 (0.);
  for (int n = 0; n < nObj; n ++) rMid += Loadv4 (4 * n).xyz;
  rMid /= float (nObj);
  ro = vec3 (0., rMid.y, -50.);
  vd = normalize (rMid - ro);
  az = 0.5 * pi + atan (vd.z, - vd.x);
  el = asin (vd.y) - 0.1 * pi;
  if (mPtr.z > 0.) {
    el += 0.5 * pi * mPtr.y;
    az += 2. * pi * mPtr.x;
  }
  zmFac = 6.5 - 3.5 * smoothstep (5., 10., rMid.y);
  ro.zy = Rot2D (ro.zy, el);
  ro.zx = Rot2D (ro.zx, az);
  ro.y = max (ro.y, 0.1);
  ltDir = normalize (vec3 (-1., 2., 1.));
  #if ! AA
  const float naa = 1.;
#else
  const float naa = 4.;
#endif  
  col = vec3 (0.);
  for (float a = 0.; a < naa; a ++) {
    uvv = uv + step (1.5, naa) * Rot2D (vec2 (0.71 / canvas.y, 0.), 0.5 * pi * (a + 0.5));
    rd = normalize (vec3 (uvv, zmFac));
    rd.zy = Rot2D (rd.zy, el);
    rd.zx = Rot2D (rd.zx, az);
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrTorusDf (vec3 p, float ri, float rc)
{
  return length (vec2 (length (p.xy) - rc, p.z)) - ri;
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

mat3 QtToRMat (vec4 q) 
{
  mat3 m;
  float a1, a2, s;
  s = q.w * q.w - 0.5;
  m[0][0] = q.x * q.x + s;  m[1][1] = q.y * q.y + s;  m[2][2] = q.z * q.z + s;
  a1 = q.x * q.y;  a2 = q.z * q.w;  m[0][1] = a1 + a2;  m[1][0] = a1 - a2;
  a1 = q.x * q.z;  a2 = q.y * q.w;  m[2][0] = a1 + a2;  m[0][2] = a1 - a2;
  a1 = q.y * q.z;  a2 = q.x * q.w;  m[1][2] = a1 + a2;  m[2][1] = a1 - a2;
  return 2. * m;
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1.,
     0., 1.), c.y);
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

const float txRow = 128.;

vec4 Loadv4 (int idVar)
{
  float fi;
  fi = float (idVar);
  return texture (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) /
     txSize);
}

// "Bouncing Chain" by dr2 - 2018
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

vec4 QtMul (vec4 q1, vec4 q2);
mat3 QtToRMat (vec4 q);
vec4 RMatToQt (mat3 m);
vec4 EulToQt (vec3 e);
mat3 LpStepMat (vec3 a);
float Hashff (float p);
vec4 Loadv4 (int idVar);
void Savev4 (int idVar, vec4 val, inout vec4 fCol, vec2 fCoord);

float todCur, nStep, ringRad, grav;
const int nObj = 16;
const int nSphObj = 18;
const float txRow = 128.;
const float pi = 3.14159;

vec3 RSph (float s)
{
  vec3 r;
  float a;
  a = (2. * pi / float (nSphObj)) * s;
  r = vec3 ((ringRad - 0.5) * cos (a), (ringRad - 0.5) * sin (a), 0.);
  return r;
}

vec3 FcFun (vec3 dr, float rSep, vec3 dv)
{
  vec3 f;
  float vRel, fo, drv;
  const float fOvlap = 500., fricN = 0.1, fricT = 1., fricS = 0.5;
  fo = fOvlap * (1. / rSep - 1.);
  drv = dot (dr, dv) / (rSep * rSep);
  dv -= drv * dr;
  vRel = length (dv);
  fo = max (fo - fricN * drv, 0.);
  f = fo * dr;
  if (vRel > 0.001) f -= min (fricT, fricS * abs (fo) * rSep / vRel) * dv;
  return f;
}

void Step (int mId, out vec3 rm, out vec3 vm, out vec4 qm, out vec3 wm)
{
  mat3 mRot, mRotN;
  vec3 rmN, vmN, wmN, dr, dv, rts, rtsN, rms, vms, fc, am, wam, rMom;
  float farSep, rSep, dt;
  dt = 0.01;
  rm = Loadv4 (4 * mId).xyz;
  vm = Loadv4 (4 * mId + 1).xyz;
  qm = Loadv4 (4 * mId + 2);
  wm = Loadv4 (4 * mId + 3).xyz;
  mRot = QtToRMat (qm);
  farSep = 2. * ringRad + 2.;
  am = vec3 (0.);
  wam = vec3 (0.);
  for (int n = 0; n < nObj; n ++) {
    rmN = Loadv4 (4 * n).xyz;
    if (n != mId && length (rm - rmN) < farSep) {
      vmN = Loadv4 (4 * n + 1).xyz;
      mRotN = QtToRMat (Loadv4 (4 * n + 2));
      wmN = Loadv4 (4 * n + 3).xyz;
      for (int j1 = 0; j1 < nSphObj; j1 ++) {
        rts = mRot * RSph (float (j1));
        rms = rm + rts;
        vms = vm + cross (wm, rts);
        dv = vms - vmN;
        fc = vec3 (0.);
        for (int j2 = 0; j2 < nSphObj; j2 ++) {
          rtsN = mRotN * RSph (float (j2));
          dr = rms - (rmN + rtsN);
          rSep = length (dr);
          if (rSep < 1.) fc += FcFun (dr, rSep, dv - cross (wmN, rtsN));
        }
        am += fc;
        wam += cross (rts, fc);
      }
    }
  }
  rMom = vec3 (0.);
  for (int j = 0; j < nSphObj; j ++) {
    rts = RSph (float (j));
    rMom += dot (rts, rts) - rts * rts + 1./6.;
    rts = mRot * rts;
    dr = rm + rts;
    rSep = abs (dr.y);
    if (rSep < 1.) {
      fc = FcFun (vec3 (0., dr.y, 0.), rSep, vm + cross (wm, rts));
      am += fc;
      wam += cross (rts, fc);
    }
  }
  rMom /= float (nSphObj);
  wam = mRot * (wam * mRot / rMom);
  am.y -=  grav;
  vm += dt * am;
  rm += dt * vm;
  wm += dt * wam;
  qm = normalize (QtMul (RMatToQt (LpStepMat (0.5 * dt * wm)), qm));
}

void Init (int mId, out vec3 rm, out vec3 vm, out vec4 qm, out vec3 wm)
{
  float a, b, s, r;
  b = 2. * Hashff (todCur + float (mId)) - 1.;
  a = 2. * pi * (float (mId) + 1.) / float (nObj);
  s = mod (float (mId), 2.);
  r = 7. - 0.7 * s;
  rm = vec3 (r * cos (a), 11. + r * sin (a), 0.);
  qm = EulToQt (vec3 (0.5 * pi + a, 0.5 * pi * s, 0.5 * pi * s));
  vm = vec3 (- 3. * rm.xz / max (0.1, length (rm.xz)), 0.).xzy;
  wm = vec3 (0.2, 0.2, 3.) * b;
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec4 stDat, qm;
  vec3 rm, vm, wm;
  vec2 iFrag;
  float tCur, mxStep;
  int mId, pxId, kp;
  bool doInit;
  iFrag = floor (fragCoord);
  pxId = int (iFrag.x + txRow * iFrag.y);
  if (iFrag.x >= txRow || pxId >= 4 * nObj + 1) discard;
  tCur = iTime;
  todCur = iDate.w;
  mId = (pxId < 4 * nObj) ? mId = pxId / 4 : -1;
  doInit = false;
  mxStep = 2000.;
  if (iFrame <= 5) {
    doInit = true;
    ringRad = 2.5;
  } else {
    stDat = Loadv4 (4 * nObj);
    nStep = stDat.x;
    ringRad = stDat.z;
    ++ nStep;
    if (nStep > mxStep) doInit = true;
  }
  if (doInit) nStep = 0.;
  grav = (mod (nStep, mxStep) >= mxStep / 4. && mod (nStep, mxStep / 4.) < 15.) ? -40. : 10.;
  if (mId >= 0) {
    if (doInit) Init (mId, rm, vm, qm, wm);
    else Step (mId, rm, vm, qm, wm);
    kp = 4 * mId;
    if      (pxId == kp + 0) stDat = vec4 (rm, 0.);
    else if (pxId == kp + 1) stDat = vec4 (vm, 0.);
    else if (pxId == kp + 2) stDat = qm;
    else if (pxId == kp + 3) stDat = vec4 (wm, 0.);
  } else stDat = vec4 (nStep, mxStep, ringRad, 0.);
  Savev4 (pxId, stDat, fragColor, fragCoord);
}

vec4 QtMul (vec4 q1, vec4 q2)
{
  return vec4 (
       q1.w * q2.x - q1.z * q2.y + q1.y * q2.z + q1.x * q2.w,
       q1.z * q2.x + q1.w * q2.y - q1.x * q2.z + q1.y * q2.w,
     - q1.y * q2.x + q1.x * q2.y + q1.w * q2.z + q1.z * q2.w,
     - q1.x * q2.x - q1.y * q2.y - q1.z * q2.z + q1.w * q2.w);
}

mat3 QtToRMat (vec4 q) 
{
  mat3 m;
  float a1, a2, s;
  q = normalize (q);
  s = q.w * q.w - 0.5;
  m[0][0] = q.x * q.x + s;  m[1][1] = q.y * q.y + s;  m[2][2] = q.z * q.z + s;
  a1 = q.x * q.y;  a2 = q.z * q.w;  m[0][1] = a1 + a2;  m[1][0] = a1 - a2;
  a1 = q.x * q.z;  a2 = q.y * q.w;  m[2][0] = a1 + a2;  m[0][2] = a1 - a2;
  a1 = q.y * q.z;  a2 = q.x * q.w;  m[1][2] = a1 + a2;  m[2][1] = a1 - a2;
  return 2. * m;
}

vec4 RMatToQt (mat3 m)
{
  vec4 q;
  const float tol = 1e-6;
  q.w = 0.5 * sqrt (max (1. + m[0][0] + m[1][1] + m[2][2], 0.));
  if (abs (q.w) > tol) q.xyz =
     vec3 (m[1][2] - m[2][1], m[2][0] - m[0][2], m[0][1] - m[1][0]) / (4. * q.w);
  else {
    q.x = sqrt (max (0.5 * (1. + m[0][0]), 0.));
    if (abs (q.x) > tol) q.yz = vec2 (m[0][1], m[0][2]) / q.x;
    else {
      q.y = sqrt (max (0.5 * (1. + m[1][1]), 0.));
      if (abs (q.y) > tol) q.z = m[1][2] / q.y;
      else q.z = 1.;
    }
  }
  return normalize (q);
}

vec4 EulToQt (vec3 e)
{
  float a1, a2, a3, c1, s1;
  a1 = 0.5 * e.y;  a2 = 0.5 * (e.x - e.z);  a3 = 0.5 * (e.x + e.z);
  s1 = sin (a1);  c1 = cos (a1);
  return normalize (vec4 (s1 * cos (a2), s1 * sin (a2), c1 * sin (a3),
     c1 * cos (a3)));
}

mat3 LpStepMat (vec3 a)
{
  mat3 m1, m2;
  vec3 t, c, s;
  float b1, b2;
  t = 0.25 * a * a;
  c = (1. - t) / (1. + t);
  s = a / (1. + t);
  m1[0][0] = c.y * c.z;  m2[0][0] = c.y * c.z;
  b1 = s.x * s.y * c.z;  b2 = c.x * s.z;
  m1[0][1] = b1 + b2;  m2[1][0] = b1 - b2;
  b1 = c.x * s.y * c.z;  b2 = s.x * s.z;
  m1[0][2] = - b1 + b2;  m2[2][0] = b1 + b2;
  b1 = c.y * s.z;
  m1[1][0] = - b1;  m2[0][1] = b1;  
  b1 = s.x * s.y * s.z;  b2 = c.x * c.z;
  m1[1][1] = - b1 + b2;  m2[1][1] = b1 + b2; 
  b1 = c.x * s.y * s.z;  b2 = s.x * c.z;
  m1[1][2] = b1 + b2;  m2[2][1] = b1 - b2;
  m1[2][0] = s.y;  m2[0][2] = - s.y;
  b1 = s.x * c.y;
  m1[2][1] = - b1;  m2[1][2] = b1;
  b1 = c.x * c.y;
  m1[2][2] = b1;  m2[2][2] = b1;
  return m1 * m2;
}

float Hashff (float p)
{
  const float cHashM = 43758.54;
  return fract (sin (p) * cHashM);
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

vec4 Loadv4 (int idVar)
{
  float fi;
  fi = float (idVar);
  return texture (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) /
     txSize);
}

void Savev4 (int idVar, vec4 val, inout vec4 fCol, vec2 fCoord)
{
  vec2 d;
  float fi;
  fi = float (idVar);
  d = abs (fCoord - vec2 (mod (fi, txRow), floor (fi / txRow)) - 0.5);
  if (max (d.x, d.y) < 0.5) fCol = val;
}
