//Ethan Alexander Shulman 2017


#define circleSize 0.04

#define draw(dc) c = mix(dc,c,clamp(d/pixelSize, 0., 1.))

#define rot(a) mat2(sin(a),cos(a),-cos(a),sin(a))



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float pixelSize = length(1.0/iResolution.xy);
	vec2 uv = (fragCoord*2. - iResolution.xy)/iResolution.x;


    vec3 c = vec3(1.);//start with white background

    //rotate rows
    uv *= rot(floor(length(max(abs(uv)-max(abs(uv.x),abs(uv.y))/2.0,0.))*16.)*iTime*0.1);

    //repeating checkered black circles
	vec2 ruv = mod(abs(uv),circleSize*2.)-circleSize;    
    float d = (length(ruv)-circleSize);
    //checker
    if (mod(dot(floor(abs(uv)/(circleSize*2.)),vec2(1.)), 2.) < 1.) draw(vec3(0.));

    //output color
    fragColor = vec4(c, 1.);
}
