 #define frame(u) camLens*(u-.5*iResolution.xy)/iResolution.y
 //2d domain space parent: https://www.shadertoy.com/view/XdffzB
 vec4 in2d(vec2 u){
  u=frame(u)*9.;
  u.y+=2.;
  vec2 f=abs(fract(u));//green carthesian grid
  float h=abs(fract(length(u)+thick)-thick);//green polar circles
  float g=min(min(f.x,f.y),h);
  d d=getd(u.x);
  //float div=sqrt(1.+p.y*p.y);
  float r=abs(d.x-u.y)/(sqrt(1.+d.d*d.d))-thick;    
  d=getd(u.x);
  //no analytical 2nd derivative, so this one sucks a bit
  float b=abs(d.d-u.y)/(sqrt(1.+d.d*d.d))-thick;
  //this does not work for scaled p, is far from being uder friendly
  float blur=20./max(iResolution.y,1.);
  vec4 yellow =vec4(vec3(1,1,0),ssb(r));
  vec4 cyan   =vec4(vec3(0,1,1),ssb(g));
  vec4 magenta=vec4(vec3(1,0,1),ssb(b));
  return aOverB(aOverB(yellow,magenta),cyan);}
