const vec3 u_samples[] = vec3[](
    vec3(0.1, 0.1, 17.0),
    vec3(0.9, 0.5, 13.0),
    vec3(0.7, 0.3, 9.0),
    vec3(0.1, 0.9, 19.0),
    vec3(0.4, 0.2, 9.0),
    vec3(0.3, 0.5, 29.0),
    vec3(0.4, 0.4, 39.0),
    vec3(0.5, 0.7, 12.0)
);

float w_i(vec2 x_i, vec2 x, float p) {
    return 1.0 / pow(distance(x, x_i), p);
}

float u(vec2 x, float p) {
    for(int i = 0; i < u_samples.length(); i++) {
        if(distance(x, u_samples[i].xy * iResolution.xy) <= 1.0) {
            return u_samples[i].z;
        }
    }
    float tmp1 = 0.0;
    float tmp2 = 0.0;
    for(int i = 0; i < u_samples.length(); i++) {
        float wi = w_i(u_samples[i].xy * iResolution.xy, x, p);
        tmp1 += (wi * u_samples[i].z);
        tmp2 += wi;
    }
    return tmp1 / tmp2;
}

float sampleValueMax() {
    float m = 0.0;
    for(int i = 0; i < u_samples.length(); i++) {
        if(m < u_samples[i].z) {
            m = u_samples[i].z;
        }
    }
    return m;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	float uv = u(fragCoord, 9.0+7.0*sin(iTime));
    float maxValue = sampleValueMax();
	fragColor = vec4(uv/maxValue * 0.9, uv/maxValue * 0.6, 1.0 - 0.9*uv/maxValue, 1.0);
}
