#define LENGTH 1.3

#define rot(a) mat2(C=cos(a),S=sin(a),-S,C)
#define TAU 6.28318531
const float sq3=sqrt(3.);
float C,S;
vec3 clr;

float spiral(vec2 uv, float angle) {
    float a=atan(uv.y,uv.x)/TAU+angle,
        l=length(uv)-a,
        s=floor(l)+a,
        c=fract(l)-.5,
        ac=abs(c),
        b=smoothstep(.27,.23,ac)*step(s,2.-1./6.);
    return b;
}

float rand(vec2 uv) { 
    return fract(sin(dot(uv,vec2(12.9898,78.233)))*43758.5453123);
}

float noise(vec2 uv) {
    vec2 i=floor(uv),f=smoothstep(0.,1.,fract(uv)),oi=vec2(0.,1.);
    return mix(
        mix(rand(i),rand(i+oi.yx),f.x),
        mix(rand(i+oi.xy),rand(i+oi.yy),f.x),
        f.y);
}

void blend(vec3 s, float a) {
    clr=s*a+clr*(1.-a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy,v=uv*(1.-uv);
    uv-=.5;
    uv.x*=iResolution.x/iResolution.y;

    vec2 st=uv*.5;
    st+=vec2(noise(st+iTime*vec2(.2,.3)), noise(st+iTime*vec2(.4,.2)+3.))*.2;

    clr=mix(vec3(.05,.3,.2),vec3(.05,.6,.8),texture(iChannel0,st).r);

    uv*=14.;
    uv*=rot(iTime*.2);

    float c=abs(length(uv)-5.3),
        w=smoothstep(.27,.23,abs(c));
    w=max(w,spiral(uv-vec2(0.,2.)*LENGTH, 0.));
    w=max(w,spiral(uv-vec2(-sq3,-1.)*LENGTH, 2./3.));
    w=max(w,spiral(uv-vec2(sq3,-1.)*LENGTH, 1./3.));
    blend(vec3(1.,.7,.4),w);

    clr=sqrt(clr)*pow(v.x*v.y*25.,.25);
	fragColor = vec4(clr,1.);
}
