// The old school demoscene effect, deformation feedback. An article from 2002
// describing it: https://iquilezles.org/articles/feedbackfx

const float th = 0.06;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 q = fragCoord/iResolution.xy;

	fragColor = 1.4*texture( iChannel0, 0.5+(q-0.5)*(1.0-2.0*th) );
}

// The old school demoscene effect, deformation feedback. An article from 2002
// describing it: https://iquilezles.org/articles/feedbackfx

const float th = 0.06;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // coords
    vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    // deformation
    vec2 uv0 = p/dot(p,p) + cos( 0.25*iTime + vec2(2.0,1.0));
    vec2 uv1 = fragCoord/iResolution.xy;

    // textures
    vec3 te1 = texture(iChannel0,fract(uv0)).xyz;
    vec3 te2 = texture(iChannel1,   0.5*uv1).xyz;

    // select color
    vec3 col = (max(abs(uv1.x-0.5),abs(uv1.y-0.5)) < (0.5-th)) 
        ?
        0.7*vec3(1.0,0.9,0.7)*te1 
        : 
        1.2*vec3(0.9,0.9,1.2)*sqrt(te2);

    // darken
    col *= min(1.5*length(p),1.0);

    // output
	fragColor = vec4(col,1.0);
}
