void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.0*fragCoord.xy - iResolution.xy) / iResolution.y;
    vec3 col = vec3(0.);

    //background
    col = vec3(.35 + (1. - (uv.y + 1.) / 2.) * 0.110, 
               .36 + (1. - (uv.y + 1.) / 2.) * 0.236, 
               .38 + (1. - (uv.y + 1.) / 2.) * 0.124);

   //star
    float starPos = noise (uv*1370.);
    starPos *= 48.;
    float starCol = clamp(1.0 - starPos, 0.0, (1. - (uv.y + 1.) / 2.));

    //sun
   	vec2 sunPos = vec2(-0.55, 0.3);
   	float sunRadius = 0.06;
   	vec3 sunCol = vec3(1., 1., 0.65);
    sunCol *= clamp(1.0 - smoothstep(sunRadius, sunRadius + 0.0000000001, length(sunPos -uv)), 0., 1.);

    //sun Blur
    vec3 sunColBlur = vec3(1., .4, .2);
    sunColBlur *= 1. - sin (clamp (length(sunPos - uv)* 0.95, .0, 1.) * 3.14/2.);

    //sunColBlur *= -log (clamp (length(sunPos - uv)* 0.95, .0, 1.) * 3.14/2.) * .4;
    sunColBlur *= 1. - clamp (pow(length(sunPos - uv), 0.5), 0., 1.3);
    //sunColBlur *= 1. - clamp (sqrt (length(sunPos - uv)), 0., 1.);

    //mountain
    //col = col * smoothstep(-0.5, -0.50000000001, uv.y + uv.x );
    //float mounCol = length(vec2(-1.0, -0.3) - uv) + length( vec2(-0.4, -0.6) - uv);
    //float lineDisc = length(vec2(-1.0, -0.3) - vec2(-0.4, -0.6));
    //col *= smoothstep(lineDisc, lineDisc + 0.00001, mounCol);

    vec3 c = max (col + sunColBlur, sunCol) + starCol;
    //vec3 c = col +starCol;

    //Tonemapping
    c.r = smoothstep(0.0, 1.0, c.r) ;
	c.g = smoothstep(0.0, 1.0, c.g );
	c.b = smoothstep(0.0, 1.0, c.b );
    c=mix(vec3(length(c)),c,1.1);

    fragColor = vec4( c , 1.);
}
