#define PI 3.1415926535897932384626433832795
#define half_PI 1.570796326794896619231321692

float max_dist_image = distance(vec2(0.5, 0.5), vec2(1.0, 0.5));
float max_dist_vr = distance(vec2(0.0, 0.0), vec2(0.25, 0.25));


vec4 render(vec2 uv, float max_dist) {
    float dist = distance(uv, vec2(0.5, 0.5)) / max_dist;
    float a = (atan(uv.y - 0.5, abs(uv.x - 0.5)) + half_PI) / PI;

    float c0 = texture(iChannel0, vec2(a, 0.25)).x;

    vec4 color = vec4(1.0, 0.0, c0, 1.0);

    if (dist >= c0) {
        color.rgb *= (c0 / dist) * 0.5;
    }

    return color;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor = render(uv, max_dist_image);
}

void mainVR( out vec4 fragColor, in vec2 fragCoord, in vec3 fragRayOri, in vec3 fragRayDir )
{
    vec2 uv = vec2(
        (fragRayDir.x + 1.0) / 2.0,
        (fragRayDir.y + 1.0) / 2.0
    );
    fragColor = render(uv, max_dist_vr);
}
