#define TAU 6.2831853
mat2 rz2(float a){float c=cos(a),s=sin(a);return mat2(c,s,-s,c);}

float hlx(vec3 p){
    float m=TAU/5.;
    float t = iTime;
    float a=mod(atan(p.y,p.x)-p.z*0.5+t,m)-.5*m;
    float l=length(p.xy);
    float r = .1+.2*(sin(abs(p.z*4.)+t*4.)*.5+.5);
    return length(vec2(a*l,length(p.xy)-(.5+abs(p.z)*2.)*(1.+sin(abs(p.z)-t*10.)*.5+.5)*0.2))-r;
}

vec3 hsv(vec3 c) {
    vec4 k=vec4(1.,2./3.,1./3.,3.);
    vec3 p=abs(fract(c.xxx+k.xyz)*6.-k.www);
    return c.z*mix(k.xxx,clamp(p-k.xxx,0.,1.),c.y);
}

float map(vec3 p){
    p.xy*=rz2(iTime*.3+length(p)*.05);
    p.yz*=rz2(iTime*.5);
    p.xz*=rz2(iTime*.7);
    float d=min(min(hlx(p.xyz), hlx(p.yzx)),hlx(p.zxy))*(1.+sin(length(p*1.)-iTime*4.)*0.8);
    vec3 n=normalize(sign(p));
    float d2=max(length(max(abs(p)-vec3(1.),0.)),dot(p,n)-2.3);
    return d;//min(d,d2);
}
float st(float x,float m){return floor(x*m)/m;}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy-.5*iResolution.xy)/iResolution.y;
    vec3 ro=vec3(uv,sin(iTime)*2.-20.),rd=vec3(uv,1.),mp=ro;
    float shade = 0.;
    int i = 0;
    const vec4 shadow = vec4(0.3);
    float t = iTime;
    for(;i<50;++i){
        float md=map(mp);
        if(md<.0001){
            //fragColor.rg *= smoothstep(0.3,0.7,sin(-t+length(mp)*10.)*.5+.5);
            break;
        }
        mp+=rd*md*.25;
    }
    //fragColor.r *= smoothstep(0.3,0.7,length(mod(mp*2.,1.)-.5)*10.);
    float r=float(i)/50.;
    fragColor = vec4(hsv(vec3(st(length(mp)*.01-iTime*0.2,6.),.8,1.)),1.);
    ///fragColor = vec4(length(mp-ro)*.08);
    //fragColor = mix(fragColor, shadow, r);
    fragColor *= 1.-r*(1.-r)*-1.;
    fragColor *= length(mp-ro)*.02;
    fragColor = 1.-fragColor;
    //fragColor=pow(fragColor,vec4(1./2.2));
    if(length(mp)>20.)fragColor=vec4(0.);
}
