void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float edges = 4.;
    float periods = 4.;
    float evolveSpeed = iTime;
    float color = noise(uv*periods + vec2(0.	, evolveSpeed), edges);
    color = noise(color - vec2(evolveSpeed), edges);
    fragColor = vec4(color);
}
