        {
             map += texture(iChannel0, uv).r*pow(clamp(1.0-length(uv), 0.0, 1.0), .5)*noise;  
            float shift = time;

            float stripes = map*5.0*PI;//*floor(log(max(iResolution.x, iResolution.y))/log(2.0));
            float black = smoothstep(0.0, .75, saw(stripes));
            float white = smoothstep(0.75, 1.0, saw(stripes));


