vec2 cmul(vec2 v1, vec2 v2) {
	return vec2(v1.x * v2.x - v1.y * v2.y, v1.y * v2.x + v1.x * v2.y);
}

vec2 cdiv(vec2 v1, vec2 v2) {
	return vec2(v1.x * v2.x + v1.y * v2.y, v1.y * v2.x - v1.x * v2.y) / dot(v2, v2);
}


float seedling;
float zoom;

vec2 mobius(vec2 uv)
{
	vec2 a = saw(seedling*vec2(1.01234*PI, GR/E*1.01*PI))*.25+.25;
	vec2 b = sin(seedling*vec2(1.0123*PI, GR/E*1.012*PI));
	vec2 c = saw(seedling*vec2(1.012*PI, GR/E*1.0123*PI))*.25+.25;
	vec2 d = sin(seedling*vec2(1.01*PI, GR/E*1.01234*PI));
	return cdiv(cmul(uv, a) + b, cmul(uv, c) + d);
}

vec2 map(vec2 uv)
{
    return saw(mobius(zoom*(uv*2.0-1.0))*2.0*PI);
    zoom = 1.0;
}

vec2 iterate(vec2 uv, vec2 dxdy, out float magnification)
{
    vec2 a = uv+vec2(0.0, 		0.0);
    vec2 b = uv+vec2(dxdy.x, 	0.0);
    vec2 c = uv+vec2(dxdy.x, 	dxdy.y);
    vec2 d = uv+vec2(0.0, 		dxdy.y);//((fragCoord.xy + vec2(0.0, 1.0)) / iResolution.xy * 2.0 - 1.0) * aspect;

    vec2 ma = map(a);
    vec2 mb = map(b);
    vec2 mc = map(c);
    vec2 md = map(d);

    float da = length(mb-ma);
    float db = length(mc-mb);
    float dc = length(md-mc);
    float dd = length(ma-md);

	float stretch = max(max(max(da/dxdy.x,db/dxdy.y),dc/dxdy.x),dd/dxdy.y);

    magnification = stretch;

    return map(uv);
}

float lowAverage()
{
    const int iters = 32;
    float sum = 0.0;

    float last = length(texture(iChannel0, vec2(0.0)));
    float next;
    for(int i = 1; i < iters; i++)
    {
        next = length(texture(iChannel0, vec2(float(i)/float(iters), 0.0)).r);
        sum += last;//pow(abs(last-next), 1.0);
        last = next;
    }
    return sum/float(iters);
}

vec2 SinCos( const in float x )
{
	return vec2(sin(x), cos(x));
}

vec2 rotatez( const in vec2 vPos, const in vec2 vSinCos )
{
	return vPos.xy * mat2(vSinCos.yx, -vSinCos.x, vSinCos.y);
}

vec2 rotatez( const in vec2 vPos, const in float fAngle )
{
	return rotatez( vPos, SinCos(fAngle) );
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float aspect = iResolution.y/iResolution.x;

    vec2 uv = fragCoord.xy/iResolution.xy;
  	vec4 sample = texture(iChannel1, fragCoord.xy/iResolution.xy);

    float height = lowAverage();

    zoom = (5.5+height*5.0);


   	const int max_i =16;
    int last_i;
    float stretch = 1.0;
    float ifs = 1.0;
    float depth = 0.0;
    float magnification;

    float total = 0.0;

    uv = rotatez(uv*2.0-1.0, height*4.0*PI+time)*.5+.5;
    seedling = height*2.0*PI-time;

    for(int i = 0; i < max_i; i++)
    {
        last_i = i;
    	float sound = height-texture(iChannel0, uv).r;
        seedling += fract(float(i)*123456.123456)+(1.0-sound*height)/PI*float(i+1)/float(max_i);

        vec2 next = iterate(uv, .5/iResolution.xy, magnification);

        //omg so platform dependent... pls help fix:
        float weight = smoothstep(0.0, 0.125, ifs);

        ifs *= smoothstep(0.0, 1.0/TIMES_DETAILED, sqrt(1.0/(1.0+magnification)));

        total += ifs;

        uv = next*weight+uv*(1.0-weight);
        float delta = sphereN(uv*2.0-1.0).z*ifs;
        depth += 1.0-delta;

		if(ifs <= 1.0E-3)
            break;
        zoom = 1.0;
        //if(mod(iTime, float(max_i))-float(i) < 0.0) break;
    }


    float sound = texture(iChannel0, saw(uv*PI+fragCoord.xy/iResolution.xy*PI+time*PI)).r;
    //fragColor = vec4(vec3(sound), 1.0);return;

    float ifsPowed = pow(ifs, 1.0/float(last_i+1));

    if(ifsPowed > .25)
    {
        fragColor = vec4(sample.rgb, ifsPowed*(1.0-height*sound));// = 0.0;
        return;
    }

    fragColor = vec4(uv, 0.0, 1.0);

    //depth /= float(max_i);
    float shift = time*PI*4.0;

    float stripes = depth*1.0+sound+height;
    float black = smoothstep(0.0, .25, saw(stripes-shift)+1.0-height*ifsPowed);
    float white = smoothstep(0.75, 1.0, saw(stripes-shift));


    vec3 final = (
        vec3(saw(stripes*PI*2.0+shift),
             saw(4.0*PI/3.0+stripes*PI*2.0+shift),
             saw(2.0*PI/3.0+stripes*PI*2.0+shift)
            )
    )*black
        +white*black;

    fragColor = vec4(vec3(ifs), 1.0);

    fragColor = vec4(saw((depth)));
    fragColor = vec4(final+height*ifs+sound*ifs, 1.0)*smoothstep(0.0, .1, (height-.125));
    fragColor.a = ifsPowed;
}

