vec2 spiral(vec2 uv)
{
    float turns = 2.0;//+saw(time*1.1234)*4.0;
    float r = pow(log(length(uv)+1.), 1.175);
    float theta = atan(uv.y, uv.x)*turns-r*PI;
    return vec2(saw(r*PI+iTime), saw(theta));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv0 = fragCoord/iResolution.xy;
    vec4 sample0 = texture(iChannel0, uv0);

    vec2 uv = uv0;

    float scale = 2.0*PI;


    float recursion1 = clamp(saw(time*PI)*3.0-1.0, 0.0, 1.0)*clamp(length(uv0*2.0-1.0), 0.0, 1.0);
    float recursion2 = clamp(saw(time*PI)*3.0-1.0, 0.0, 1.0);

    uv = ((spiral(scale*(spiral(scale*((uv)*2.0-1.0))*2.0-1.0))*2.0-1.0)*recursion1+(1.0-recursion1)*(uv*2.0-1.0))
        	*(1.0-recursion2)+recursion2*(saw(uv*PI*(5.0+sin(time)*5.0))*2.0-1.0);

    vec2 direction = uv*iResolution.xy/60.0;

    vec4 sample = texture(iChannel0, uv0+direction/iResolution.xy*sample0.a);

    fragColor = (sample)*sample0.a+sample0*(1.0-sample0.a);
    fragColor.rgb -= 1.0/255.0;
}
