#define sdCircle(p, r) length(p)-r
#define dAdd(d1, d2) min(d1,d2)
#define mov(xy, t) xy-t
#define asLine(d, thickness) smoothstep(thickness,0.,abs(d))

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (2.*fragCoord-iResolution.xy )/iResolution.y;
	vec4 m = vec4(2.*iMouse - iResolution.xyxy ) /iResolution.y;

    float objects = max(mod(m.x, 500.)+100., mod(m.y, 500.))+20.;
    float d = 1000.;
    float ds = d;

    for(float i=0.;i<objects;++i) {
    	float fi = float(i);
        vec2 loc = mov(uv, vec2(sin(fi/mod(m.x,9.)+iTime)*1.9, sin(fi/mod(m.y,9.)+iTime*.5))*.8);
    	ds = dAdd(ds,sdCircle(loc,.2));
    }

    d = asLine(ds,0.3);

    fragColor = vec4(vec3(sin(iTime)*0.5, 0.5*d, 0.7*ds), 1.);
}
